INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132862,"Karang ta baluak di parau","Kulik-manih di dalam padi","Larang makaluak nan ka tau","Dagang ma-nangih dalam ati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132863,"Karang tertumbuk di perahu","Kulit manis di dalam padi","Larang makhluk yang akan tahu","Dagang menangis dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132864,"Karangan bunga mari dihidu","Dalam keramaian di Tapanuli","Apa sudahnya pungguk merindu","Sedangkan bulan tak ambil peduli?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132865,"Karangan termasyhur Imam Shafie","Tersebar luas keseluruh dunia","Hidup macam ahli sufi","Di akhirat hidup penuh bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132866,"Karangkan kami bunga pandan","Telur sepat dideraikan","Sorakkan kami di tengah medan","Di batin jangan diceraikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132867,"Karangkan kami bungo pandan","Talua sapek padaraikan","Sorakkan kami tangah medan","Di batin jangan dicaraikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132868,"Karangnya banyak timbul tenggelam","Itulah tempat purbakala","Terang laksana permata nilam","Boleh dibawa tajuk gemala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132869,"Karangnya banyak timbul tenggelam","Itulah tempat purbakala","Terang laksana permata nilam","Boleh dibuat tajuk gemala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132870,"Karano gadai pindah harato","Atau dek jua atau pun sando","Agiah jo hibah baitu pulo","Tangga hak miliak baganti nan punyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132871,"Karatang ranang di aluh","Anak pipit mandi seberang","Kami menatang minyak yang penuh","Setitik tidak diberi kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132872,"Karatau di dalam rimbo","Nyo gatia anak rang di Ampang","Satu tibo amak batanyo","'Bato lali mek kau pulang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132873,"Karatau di dalam rimba","Dipetik anak orang Ampang","Baru sampai ibu bertanya:","'Mengapa terlambat kau pulang?'", 4, 6);
