INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132879,"Karatau usah disangko madang","Kadang-kadang baluluah padi","Marantau usah disangko sanang","Kadang-kadang barusuah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132880,"Karateh turab aia ameh","Ula garang rono limbayuang","Ja-an tuan ba-ati cameh","Sungguah paneh ado kalinduang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132881,"Karbouwen van Toba","Weiden daar buiten","Daar waar geen lust is","Zijn uitvluchten talrijk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132882,"Karena ada kesamaan visi","Dalam menuju hidup berarti","Tentu tegak sebuah misi","Mengungkap suatu kebesaran Ilahi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132883,"Karena ayam lupakan taji","Banyaklah musang menahan jerat","Karena imam lupakan kaji","Banyaklah orang kerja maksiat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132884,"Karena bayan bertambah garang","Banyaklah pungguk jadi berdendam","Karena hutan dirambah orang","Banyaklah beruk tak tidur malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132885,"Karena beruk duduk melukah","Banyaklah kijang datang mengintai","Karena datuk tertangkap basah","Banyaklah orang bercerai berai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132886,"Karena dapat tali berenda","Tidak perlu ke pasar lagi","Karena mendapat bini janda","Tidak perlu belajar lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132887,"Karena ikan bertambah marah","Banyaklah udang lari kedarat","Karena hutan punah dirambah","Banyaklah kijang jadi melarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132888,"Karena ilmu anugerah Tuhan","Sifatnya mencari kebenaran","Hakekat tertinggi setiap penemuan","Suatu tanda adanya kebesaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132889,"Karena itu Tuhan Yang Esa","Menyeru ummatnya menegakkan keadilan","Kapan waktunya tak pilih rasa","Senantiasa tentram akan berkekalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132890,"Karena jodoh di tangan Tuhan","Itu terbaik jadi pilihan","Kehormatan istri jangan di abaikan","Dengan merampas sebuah kehangatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132891,"Karena kacip disangka pisau","Banyaklah pinang dilantak ulat","Karena khatib suka meracau","Banyaklah orang tidak berjumat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132892,"Karena kainnya sudah koyak","Hendak diikat pastilah kendur","Karena pemimpin loba dan tamak","Banyaklah umat mati kebulur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132893,"Karena kainnya tiada lebar","Hendak diikat pastilah tanggal","Karena pemimpin pendusta besar","Banyaklah umat mati terjual", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132894,"Karena kayu dilanda badak","Banyaklah kijang mengumpat marah","Karena penghulu gila bertandak","Banyaklah orang tak dapat nikah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132895,"Karena kayu lapuk berlubang","Banyaklah ulat dan anai-anai","Karena penghulu mabuk kepayang","Banyaklah helat tidak selesai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132896,"Karena kera bengak berbual","Banyaklah ungka jadi mengantuk","Karena tanah banyak dijual","Banyaklah rusa pergi merajuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132897,"Karena kera hendak berbaju","Banyaklah kijang memberi batik","Karena rajanya tidak bermalu","Banyaklah orang menjadi fasik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132898,"Karena kera hendak menikah","Banyak lembu merebus daging","Karena mertua tamak serakah","Banyak menantu kurus kering", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132899,"Karena kucing memakan nangka","Banyaklah musang tak dapat rezki","Karena bersaing dengan yang tua","Banyaklah bujang tak dapat bini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132900,"Karena mulut badan binasa","Terlanjur kata itu bahaya","Suka menghina tiada dirasa","Ditinggalkan kawan handai sahaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132901,"Karena pengakuan si miskin","Ilmu hanya satu titik saja","Hendak aku kumpulkan titik lain","Agar insan memahami Yang Maha Ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132902,"Karena tenuk makan serakah","Badak heran punggukpun bengis","Karena menengok hutan dirambah","Banyaklah hewan duduk menangis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132903,"Karena tugal disangka atan","Banyaklah orang tidak ke ladang","Karena bilal lupakan azan","Banyaklah orang tidak sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132904,"Kari ayam campur kentang","Tumis rernpah bersama susu","Di kampus suaranya lantang","Kini di padang lesu bisu!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132905,"Kari Sutan rang Koto Gadang","Mantari Rangkiang Luluih","Kok nak tau di untuang dagang","Lukah pasuak karuntuang bubuik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132906,"Karieng si tangguak","jo si-dukuang","Ka di-buang kapalo-nyo","Ka ba-cakak rang jo untuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132907,"Karih bahulu dangan perak","Bantuak kapalo ula nago","Sakato dahulu niniak mamak","Sarato dangan ibu jo bapo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132908,"Karih bahulu si Bintang Timua","Bacalak salimpo alam","Padang panghulu lah tamusahua","Jauah jo dakek inyo lah paham", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132909,"Karih banamo si Samparono","Bingkisan rajuah","Kaduo anggo jo tango","Iyolah limbago nan sapuluah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132910,"Karih bapantang diantakkan","Karih mancamuak sandirinyo","Takilek sajo bak roman ikan","Lah tantu jantan batinonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132911,"Karih bapatang diantakkan","Karih mancamuak sandirinyo","Sapandai-pandai urang mailakkan","Dibaliak pulau kanai juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132912,"Karih sampono ganjo erah","Lahia batin pamaga diri","Patah lidah ka bakeh alah","Patah karih ka bakeh mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132913,"Karih sinjato kabasaran","Sisampiang jo cawek nan tampeknyo","Sisiknyo tanam tabukan","Ka kida condong lataknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132914,"Karikam banyak karikam","Kamuniang bakarek-karek","Nan itam banyak nan itam","Nan kuniang banyak cilalek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132915,"Karisiak perai tanam di julang","Padi dibaruah rabah runtuah","Kasiah bacarai sabab dek urang","Ati di dalam tambah rusuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132916,"Karisiak tadorong ka ilalang","Balimau puruiklah daulu","Kasiah tadorong lah tabuang","Ganjua suruiklah daulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132917,"Karkun seri wangsa","Hulubalang raja Azrang","Mohonkan ampun dengan periksa","Tuanku tidak larangan orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132918,"Karna karang batang aloean","Oleh batang la kayoe djatie","Atie mendoa kapada toean","Lebe' boe la saya matie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132919,"Karna kelasi kapalku pecah","Sedang menuju ke pantai Bali","Karna kekasih hati merana","Daku tak mau bercinta lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132920,"Karna nona la saija tjienta","Djangan nona beratie sosa","Perkataan soengoe la saya minta","Biear terikat kita berdoea", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132921,"Karok-karok kalapo puan","Tak puan kalapo bali","Ambo arok pado tuan","Tak tuan siapo lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132922,"Karap-karap kelapa puan","Tak puan kelapa Bali","Saya harap kepada tuan","Tak tuan siapa lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132923,"Karonggoh hilir barakik","Taparakik ka ranting iduik","Masuk naroko palang dek sakik","Baraso kambing dibobak iduik", 4, 6);
