INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133656,"Kelapa muda sudah dibelah","Ambil isinya kulit buanglah","Bersaksi tiada Tuhan selain Allah","Nabi Muhammad adalah Rasulullah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133657,"Kelapa muda tidak berminyak","Nyiur gading hiasan taman","Sedikit kerja cakapnya banyak","Bukan tanding tukang budiman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133658,"Kelapa muda turun sebiji","Airnya manis hilang dahaga","Cergas bersukan pandai mengaji","Guru sayang ayah pun bangga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133659,"Kelapa parut di dalam baldi","Ke Sulawesi mencari tanduk","Elok diturut resmi padi","Semakin berisi semakin tunduk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133660,"Kelapa pua kelapa Bali","Bawa belayar ke negeri India","Hati perempuan bak roti Benggali","Terkena air lembiklah dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133661,"Kelapa puan dalam perahu","Hendak dibawa untuk jualan","Kalau tuan ingin nak tahu","Turun bawah saya terangkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133662,"Kelapa puan di dalam tangki","Hendak dijaja Kampung Pandan","Suara perempuan tetapi lelaki","Namakan saja si Pop Pondan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133663,"Kelapa puan di dalam tangki","Hendak menjaja di Kampung Pandan","Suara perempuan tetapi lelaki","Namakan saja pop pondan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133664,"Kelapa puan di tengah laman","Pokoknya tua tidak berjaga","Ingatlah tuan orang budiman","Majukan bangsa peliharalah agama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133665,"Kelapa puan kelapa Bali","Bawa belayar ke negeri India","Hati perempuan bak roti Benggali","Terkena air lembiklah dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133666,"Kelapa puyuh sudah dikukur","Lemak bubur dicampur kicap","Nasib berkayuh sampan bocor","Bagai dapur tidak berasap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133667,"Kelapa satandan tinggal ampat","Kandak masak sayor keladi","Buah di-idam kalu tak dapat","Susah menanggong di-dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133668,"Kelapa sawit dipanggil Bali","Sabit diusung hingga ke senja","Orang berduit semua dibeli","Si kocek kosong melihat saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133669,"Kelapa sawit membawa keuntungan","Terutama Guthrie dan Felda","Puak Hadhari lepas tangan","Orang Melayu murtad berlipat ganda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133670,"Kelapa sawit serta nenas","Dekat Keluang ditanam orang","Cacing kerawit bila mengganas","Muka yang merah bertukar perang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133671,"Kelapa sawit tumbuh berjela","Ramai pekerja sampai ke Beluran","Wang ringgit bukanlah segala","Iman juga menjadi ukuran (Kumpulan Pantun Sabah) (Agama)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133672,"Kelapa tua airnya pedar","Tidak sepedar si buah pala","Yakin pula akan qada' dan qadar","Yang di gariskan Allah ta'ala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133673,"Kelapa tua diparut","Santannya boleh ditakar","Bukan hanya perut","Pikiran pun merasa lapar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133674,"Kelapa tua masih se tandan","Ambil dua dibuat santan","Kita berlindung dari godaan","Yang nama makhluk jin dan syaithan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133675,"Kelap-kelip api di kapal","Anak Belanda memasang pelita","Air mata menitis ke bantal","Baru kusedar hatiku cinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133676,"Kelap-kelip api di kapal","Nampak dari atas kota","Air mata jatuh ke bantal","Baru kutahu hati bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133677,"Kelap-kelip api di kapal","Nampak dari di atas kota","Air mata jatuh ke bantal","Baharu kutahu hati bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133678,"Kelap-kelip lampu di kapal","Kain cina di kebun jati","Macam mana rasa tak sebal","Lain kata lain di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133679,"Kelaq aiq leq balen amaq alim","Kadu nyedok indomi","Lamun side pake muslim","Kereng-kereng silaturrahmi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133680,"Kelaq me keang pemongkaq","Bau pelisaq jangan rai","Pada rebakti lalo Inaq Bapak","Ama naq loq sesal mui", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133681,"Kelaq nangka masaq odaq","Toloq tiang leq sempare","Sai ingkar leq dengan toaq","Iye jari idup sengsare", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133682,"Kelaq nangke kelak lomaq","Tokok sie leq sempara","Ndak bangga leq inaq amaq","Laun idup jari sengsara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133683,"Kelasa tampak berendam","Tampak nan dari Batu Berdaun","Tiada kuasa menanggung dendam","Habis bulan berganti tahun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133684,"Kelasa tampak berendam","Tampak nan dari Batoe Berdaoen","Tidaj koeasa menanggoeng dendam","Habis boelan berganti tahoen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133685,"Kelasa tampak berendam","Tampak nan dari Batu Berdaun","Tidak kuasa menanggung dendam","Habis bulan berganti tahun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133686,"Kelasa tampak merendam","Tampak dari Batu Berdaun","Tidak kuasa menanggung dendam","Habis bulan berganti tahun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133687,"Kelasi apa kapalnya","Kelantan pergi tujunya","Tuah besi nampak tagarnya","Tuah intan nampak mercunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133688,"Kelasi di geladak berpusu-pusu","Melihat rusa di tebingan","Menangis budak dahaga susu","Menangis dewasa apa gerangan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133689,"Kelasi menggubah anggerik cerana","Semerbak disantap harum terpateri","Firdausi muhibbah gundah gulana","Sebak ditatap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133690,"Kelat layu lebat s'dahan","Sayang s'lasay di tanjong jati","Banyak malu bolay ku tahan","Ber-cheray kasay rasa nak mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133691,"Kelat sepat si buah badam","Kelat lagi si buah rokam","Sengketa kalau tidak dipadam","Bagai api di dalam sekam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133692,"Kelebor terkenal zaman dahulu","Sebagai tanda bencana alam","Atau menaruh segan dan malu","Menjalankan usaha siang dan malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133693,"Keledai di dalam kota","Pergi petang menanggung rumput","Tidak sampai barang dicita","Modalnya banyak setengah luput", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133694,"Keledek digaul gula putih","Kuih keria bersalut bijan","Wajah anak penambat kasih","Bak udara selepas hujan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133695,"Keledek rnerah bersama beremi","Dimasak lemak kurangkan garam","Jangan ditumpang perahu kami","Perahu usang menanti karam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133696,"Keledek tiga buku","Pergi blayar bawa bekal","Barang baik dras la laku","Barang yang busok krap tertingal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133697,"Kelekati bermata ruing","Akan mengacit si tawang-tawang","'Nak mati berbulan terang","'Nak berkubur di awang-awang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133698,"Kelekati dari pekan","Orang mandi jangan intaikan","Main judi jangan amalkan","Orang berbudi mesti ingatkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133699,"Kelelawar di waktu malam","Terbang keliling di pucuk pisang","Lagi hidup ada harapan","Sudah mati tinggal pikiran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133700,"Kelelawar terbang malam","Digigit semut merah menjahang","Tidak mau kami semua","Sampai tungku pun menggeleng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133701,"Kelem Jum'at kelem sejarah","Ongkat dengan toaq lueq hantu","Dendeq nangis amun te bepisah","Lamun jodoh pasti betemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133702,"Kelem kemis te belajar","Minaq pantun basa Sasak kentel","Lamun te bareng ndeq te kurang ajar","Sengaq erak siq te bejoan taoq te pade nyesel", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133703,"Kelem Minggu lalo midang","Lalo midang kadu montor","Nangke kepeng rakyat bueq ilang","Sengaq te kaken isiq koruptor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133704,"Kelem Minggu te lalo midang","Jauq radio jari selingan","Te bebaturan ndeq te riang","Siep bejao'an jori tantangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133705,"Kelemaq-lemaq nenalet padi","Tengsari-ngari bekelor jangan kelaq","Lamun ndeq mele nyesel leq jelo mudi","Ndaq lupaq leq Inaq Amaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133706,"Kelembak nama bau-bauan","Parang ditetakkan ke dahan sena","Kalau nama tidak ketahuan","Barang kerja tidak sempurna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133707,"Kelembak nama bau-bauan","Tetakkan parang ke dahan sena","Kalau nama tidak ketahuan","Barang kerja tidak sempurna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133708,"Kelem-kelem aning bale papuq","Soq uwah jauq ketujur","Agen meq taoq pade kelapuq","Telu penyaong rebaq siqku jagur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133709,"Kelem-kelem beli senter","Senter te beli leq Mekah","Ape guna te pede pinter","Lamun endeq uwah beribadah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133710,"Kelemunting di Tanjung Pauh","Kapal sarat dimuat jerami","Ada untung digantung sauh","Kalau tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133711,"Keleq santen mieq cecengeh","Ndek lupek ngelek nasi","Dendeq bengaq lamunne dengan becat sugeh","Sengaq pegaweanne korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133712,"Keli bertapa di dalam lubuk","Lubuk jerung dilarang mandi","Bagai kelapa sudah ditebuk","Kera lotong pun tak sudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133713,"Keli bodoh hilang lubuk","Sepat seekor berenang ke hulu","Bukan jatuh melanggar tembok","Saya tersungkur disaman malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133714,"Keli di paya payah dijerat","Mudah dikail di air tenang","Pokok budaya bunganya adat","Dirisik dulu baru dipinang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133715,"Keli haruan dan sepat","Sedap dibuat gulai tempoyak","Betul aqidah pahala dapat","Seperti bercakap yang hak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133716,"Kelicap kecil bermuncung tajam","Menyedut madu di setiap bunga","Baru bertemu terus meminjam","Dari duit ke periuk belanga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133717,"Kelihatan hijau dari jauh","Sejuk mata bila terpandang","Modalnya aqidah yang teguh","Ingat akujanji sewaktu sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133718,"Keliki ambil petanak","Air mendidih kadangi nasi","Air jernih ikannya jinak","Bolehkah diri mandi di sini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133719,"Keliki ambil petanak","Air mendidih kadangi nasi","Air jernih ikannya jinak","Bolehkah diri mandi di sini?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133720,"Keliki ambil petanak","Ayer mendideh rebus berangan","Ayer jerneh ikannya jenak","Hendak ditangkap lepas ditangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133721,"Keliling dewan kencana puri","Balai Nobat dekatlah sangat","Mesra berkawan hidup berseri","Akrab sahabat perkukuh semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133722,"Keliling kampung menjaja kuih","Kuih talam kuih baulu","Resmi ombak menjunjung buih","Adat muda menanggung rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133723,"Keliling ke perigi","Di dalam paya tanam birah","Semut tenggiling tiada gigi","Buaya tiada lidah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133724,"Keling Jawa bangsa Surati","Dijual Cina kepada Melayu","Rindu berkurung dendam menanti","Siang dan malam dendam terpayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133725,"Keling keling paku","Paku masak lemak","Jangan jeling aku","Aku bilang emak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133726,"Kelingkang digaga udah sedia","Perengka diator sama sereta","Piring diengkah atas reraga","Baru utai bukai diambi iya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133727,"Kelip kelip di sangka api","Kalu api mana pontong nya","Ilang raip di sangka mati","Kalu mati mana kubor nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133728,"Kelip kelip ku sangka api","Kalu api mana pontong nya","Elang raip ku sangka mati","Kalu mati mana kubor nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133729,"Kelip-kelip api di dusun","Anak Keling bergelang kaca","Bukan mati kerana racun","Mati dijeling ekor mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133730,"Kelip-kelip api di dusun","Anak kucing bergelang kaca","Bukan mati kerana racun","Mati dijeling ekor mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133731,"Kelip-kelip api di kapal","Nampak dari di atas kota","Aying mata jatuh ke bantal","Baruku tahu hati bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133732,"Kelip-kelip api di kapal","Nampak mari di atas kota","Aying mata jatuh ke bantal","Baruku tahu hati bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133733,"Kelip-kelip di hutan rimba","Sambaq-sambaran ke belakang perahu","Kukerjit-kerjit bukan sengaja","Seumoq hidup mataku begitu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133734,"Kelip-kelip di Tanjong Kidurong","Kapal Jepun memunggah arang","Sudah nasib tiada beruntung","Bunga di kebun dipetik orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133735,"Kelip-kelip disangka pelita","Penuh di pokok sepanjang muara","Buah hati segunung cinta","Jadi isteri kenapa didera?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133736,"Kelip-kelip 'ku sangka api","Kalau api mana dapurnya?","Adik kecil 'ku sangka mati","Kalau mati mana kuburnya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133737,"Kelip-kelip ku sangka api","Kalau api mana dapoqnya","Tuan pi ku sangka ke mati","Kalau dah mati mana kuboqnya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133738,"Kelip-kelip kusangka api","Anak kerbau mati terjalar","Tut! Tut! Tut! Ia berbunyi","Badannya panjang umpama ular", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133739,"Kelip-kelip kusangka api","Api bertambun di atas batu","Tuan ghaib kusangka mati","Takdir suratan kita bertemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133740,"Kelip-kelip kusangka api","Kalau api mana pungtungnya","Orang ghaib kusangka mati","Kalau mati mana kuburnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133741,"Kelip-kelip kusangka api","Kalau api mana puntungnya","Hilang ghaib kusangka mati","Kalau mati mana kuburnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (133742,"Kelip-kelip kusangka api","Kalau api mana sumbunya","Hilang ghaib kusangka mati","Kalau mati mana kuburnya", 4, 6);
