INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134435,"Koenang-koenag beriboe riboe","Djato di kebon di manja doewa","Toenangan satoe di reboet reboet","Tjaboet pedang di bela doewa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134436,"Koetjieng koeroes mandie di papan","Papan nja lagi si kayoe djatie","Boekan koeroes jang koerang makan","Koeroes iengat si djantoeng atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134437,"Kok adat babuhua sentak","Sakato mangko dijalankan","Luruih tak dapek ka mailak","Hukum nan bana dituruikkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134438,"Kok adiak rarak kamba rotan","Tujuah sajaja atua peti","Kok bakucak ati tolan","Cari nan baiak tampek ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134439,"Jika adik buka kembaran rotan","Tujuh sejajar susunan peti","Jika berguncang hati tolan","Cari yang baik tempat hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134440,"Kok ado raso handak kuhua","Baok malengah sabalah kida","Usah tasambua aia liua","Dindiangkan tangan sabanta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134441,"Kok ado samba kurang garam","Atau pun bumbu kapadasan","Atau kok gulai ado nan masam","Usah dicacek baadok-an", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134442,"Kok ado sumua di ladang","Buliah kito manumpang mandi","Ampun jo maaf minta pangarang","Di siko gurindam disudahi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134443,"Kok ado sumua di lading","Ka bakeh kito barulang mandi","Kok ado umua samo panjang","Insya Allah disambuang kaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134444,"Kok ado sumua di lading","Manumpang mandi malah kito","Kok ado umua samo panjang","Di lain kali disambuang pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134445,"Kok ado sumua di Padang","Marilah kito tanami padi","Kok umua samo panjang","Aroklah adiak batamu lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134446,"Kok alang kapalang santun","Indak manta sampai-sampai","Buek putusan bak mahukum","Amak jan tingga jo bangkalai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134447,"Kok alek ado bajuaro","Nan manduduakkan sagalo jamu","Cilako tuo tigo parkaro","Partamo nyinyia tak manantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134448,"Kok baalemu kapalng paham","Angan panjang itikad salah","Budi marangkak kok kurang dalam","Gunuang tinggi disangko lurah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134449,"Kok bak nangko runtuahnyo padi","Ka baraik condong buahnyo","Kok bak nangko cando susahnyo ati","Mularaik kasudahannyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134450,"Kok bakato elok marandah","Lamak di lua kuaik di dalam","Muluik manih kucindan murah","Muko janiah indak padandam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134451,"Kok balinduang cari nan taduah","Kalau bapadan nan saukua","Ingek tagamang jan sampai jatuah","Agak sabalun kato talanjua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134452,"Kok barabuak iyo bajantiak","Kalau rasan dilengganginyo","Panghulu condong ka nan baiak","Malatakkan sasuatu di tampeknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134453,"Kok barek samo dipikua","Kalau ringan samo dijinjiang","Hasia karajo dek saukua","Hilang nan barek dek barundiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134454,"Kok barek samo dipikua","Kalau ringan samo dijinjiang","Kok buek samo saukua","Hasia mupakat jo barundiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134455,"Kok basuo gajah mandi","Tanamlah pisang ka ulu aia","Kok basuo tampek ati","Pantun utang sudah babaia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134456,"Kok buliah candu ka paisok","Ka lambah parang jajaknyo","Kok buliah kayu ka painggok","Labah basarang di puncaknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134457,"Jika dapat candu buat dihisap","Ke lembah terlihat jejaknya","Jika dapat kayu tempat hinggap","Lebah bersarang di puncaknya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134458,"Kok buliah sakandak padi","Banda baranjak ka tapian","Kok buliah sakandak ati","Gunuang baranjak ka tapian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134459,"Kalau boleh sekehendak padi","Bandar beranjak ke tepian","Kalau boleh sekehendak hati","Gunung beranjak ke tepian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134460,"Kok bulieh karambia tumbuah","Di-tanam di bawah janjang","Kok bulieh kato nan sungguah","Di-gungguang di-bao tabang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134461,"Kok condong tungkek manungkek","Kalau rabah samo tagakkan","Kok panjang karek mangarek","Kok singkek samo paulehkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134462,"Kok dagang darek batapatan","Kok dagang lawik balabuhan","Jauah nan jadi badangaran","Dakek nan jadi bahimbauan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134463,"Kok dicurai dipapakan","Dalam kulik lah tampak isi","Dangka kok alah kasubarangan","Mamak nan hindak baharago lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134464,"Kok dikaji tantang si rajo janang","Tahu dihereng dangan gendeng","Tahu dicakah dangan kaik","Tahu manjago labiah jo kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134465,"Kok dipanggia ka lakeh datang","Kalau disuruah ka lakeh pai","Bia barugi barang samiang","Pitih sakupang jan dikanai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134466,"Kok duduak ateh kandaraan","Tangan tasampai bak manggawai","Caliaknyo tinggi manabuak awak","Dunia bak raso ka dikisai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134467,"Kok galak sarupo baalamat","Pandang saroman malawani","Binaso mudo jan diniat","Iman ka goyang handak hindari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134468,"Kok gumpo antaro karok","Kok karok masuak ragian","Kok lupo antaro lalok","Kok lalok masuak rasian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134469,"Jika gumpal antara karap","Jika karap masuk raglan","Jika lupa antara lelap","Jika lelap masuk rasian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134470,"Kok ikan iyo basiriang","Kalau buruang iyo bakapak","Kok sawah iyo bapiriang","Kalau lading iyo babintalak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134471,"Kok indak ado barado","Tak tampuo basarang randah","Kok indak basabab bakarano","Tak limbago ka barubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134472,"Kok indak kanai dek ujuangnyo","Kanai singanga ka mati juo","Kok tak tatangkok tangan kasalahannyo","Tando beti ka dapek juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134473,"Kok indak satampok jo sajangka","Baru nan saheto atau sadapo","Pusako tinggi tak buliah dijua","Kalau gadai ado syaratnyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134474,"Kok indak talang bapapek","Antah talantak rigo-rigo","Kok indak gunuang maambek","Antah tampak inyo dari siko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134475,"Kok Irak Iran dahulu","Judian kito jaloi","Kok indak diam dahulu","Kudian kito mulai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134476,"Jika Irak Iran dahulu","Judian kita jalai","Jika tidak diamlah dahulu","Kemudian kita mulai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134477,"Kok iyo kito kan mandi","Ambiek palekat di ate salang","Kok iyo kitokan mati","Kaji tarikat hondak dipatorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134478,"Kok iyo pai ka rimbo","Dama cari rotan pun cari","Partamo baharato pambao","Dibao laki ka rumah bini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134479,"Kok iyo salasiah Jambi","Tanam karakok tapi pamatang","Kok iyo kasiah di kami","Kirimkan rokok tigo batang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134480,"Kok jadi bujang ka pakan","Iyu bali balanak bali","Kok pai bujang bajalan","Ibu cari dunsanak cari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134481,"Kok jadi pai ba-ladang","Kain panjang cari daulu","Kok jadi pai ba-dagang","Induak samang cari daulu (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134482,"Kok jauah datang diulangi","Kok dakek tantu dikandano","Kaduo barang diwarisi","Dari muyang turun pusako", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134483,"Kok kandua roman tali jalo","Raso ka elok ditagangi pulo","Agaklah duduak di partangahannyo","Baitu urang babicaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134484,"Kok karok masuak ragian","Taerak tairo pulo","Kok lalok masuak rasian","Tasintak takiro pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134485,"Jika karap masuk ragian","Terbuka terancang pula","Jika lelap masuk rasian","Tersentak teringat pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134486,"Kok kelek kok","Ayam jambul emas","Buat apa tuk","Duduk sayur nanas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134487,"Kok kerek kok","Ayam balung emas","Nanti dulu tuk","Nak kirim subang emas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134488,"Kok ketek bari banamo","Kok gadang bari bagala","Nak tapek adaik jo limbago","Paham adaik nan nyato bana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134489,"Kok labiah kanyang dari kalaparan","Hilang pulo malah pikiran","Taruah naraco jo katian","Pitua guru handak amalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134490,"Adapun kato pagaulan","Partamo kato mancari kawan","Kaduo kato mancari lawan","Katigo kato tak balawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134491,"Kok labiah mulia dari hino","Takabua tibo kamudian","Partamo kato takato-kato","Indak sangajo mangatokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134492,"Kok labiah rajin dari sagan","Mako datanglah kabajikan","Kaduo kato nan dikatokan","Iyolah sangajo mangatokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134493,"Kok lalu di jalan rayo","Kalau mandi di aia janiah","Mahukan adia 'bana' katonyo","Baiak jo buruak nan basisiah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134494,"Kok lambang padi di balam","Banalak banci juo","Kok rambang ati di dalam","Jo galak manihi juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134495,"Jika rusak padi keranabalam","Benalak bencikan jua","Jika hancur hati di dalam","Dengan gelak maniskan jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134496,"Kok lantak sabana lantak","Nan lungga diarekkannyo","Kok latak sabana latak","Malatakkan sasuatu di tampeknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134497,"Kok larak indak ka luruik","Asa jan badarai padi","Kok sarak indak ka suruik","Asa jan bacarai mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134498,"Kok mandi mandi bak kini","Mandi batimbo mangkuak Jao","Kok mati mati bak kini","Gilo baati mabuak juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134499,"Kok mandi mandi bak kini","Sikujua balah paduo","Kok mati mati bak kini","Sakubua kito baduo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134500,"Kok manih jan salah lulua","Paik nan nambek dimutahkan","Bago batuka dek maukua","Malang kok senjang dikanakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134501,"Kok naiak iyo dari janjang","Kok turun iyo dari tanggo","Kalimo ado bagalanggang","Tampek batamu basuko-suko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134502,"Kok nak tahu di ladang padi","Di Panti jalan ka Cubadak","Kok nak tahu di hati kami","Lieklah api makan dadak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134503,"Kok nak tau di Koto Gadang","Ma-nurun dari Bukik-Tinggi","Tuan man-jadi buruang tabang","Kami man-jadi kayu tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134504,"Kok nak tau mam-balah mumbang","Balah-lah je ampo kuku","Kok nak tau di jinak kumbang","Tanamlah bungo tapi pintu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134505,"Kok nan lamah batueh","Kalau nan condong batungkek","Kok hanyuik bapinteh","Kalau tagamang bajawek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134506,"Kok nyampang sampai bapambayan","Karano laki babini baduo","Nasib tak dapek diilakkan","Suami dijapuik urang pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134507,"Kok oyo kito mandi","Ambiek pariket di ate salang","Kok iyo kitokan mati","Kaji tarikat hondak dipatorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134508,"Kok pai samo satampuah","Kalau pulang samo sabondong","Kok basamo indak nan jauah","Sakik sanang buliah baretong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134509,"Kok pandai bakain panjang","Labiah saelok kain saruang","Kok pandai bainduak samang","Labiah saelok mandeh kanduang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134510,"Kok pandai man-jaik-sangkuik","Ba-darai kulindan suto","Ba-baua kok alun patuik","Ba-carai ati-lah iba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134511,"Kok pandai tagak di majun","Saraso tagak di kamudi","Kok pandai maminum racun","Sarasa santan jo tangguli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134512,"Jika pandai tegak di majun","Serasa tegak di kemudi","Jika pandai meminum racun","Serasa santan dengan tengguli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134513,"Kok panjang makanan karek","Kok singkek patuik diuleh","Kalau tak langkok rukun jo sarat","Sagalo karajo ilang laleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134514,"Kok patah kapak tacabuik bulu","Siang jo malam dek baganti","Pandai manenggang nan caro itu","Hiduik baniago susah sakali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134515,"Kok putuih putuih rang uleh","Ka kabek pinggang Sinaro","Si Rajanun namo-nyo unggeh","Iko nan pandai ba-bicaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134516,"Kok riak riam di ulu","Aia tanang kito jaloi","Kok indak diam dahulu","Malah tanang kito mulai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134517,"Kalau riak riam di hulu","Air yang tenang kita jalai","Kalau tidak diamlah dahulu","Bila tenang kita mulai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134518,"Kok sakik arangkan surang","Padiah tak samo manyiuakkan","Buruak baiak indak ditimbang","Budi kurang tak tahu sopan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134519,"Kok salah sangko urang di kampuang","Taserak buruak ka nagari","Badan damam guruah badantuang","Sakik tak mungkin sambuah lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134520,"Kok samo tinggi kayu di rimbo","Ka mano angin kabaputa","Pariuak sanduak lai balago","Kok kunun pulo manusia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134521,"Kok sampai upiak bajunjungan","Paelok parangai jo taratik","Pabaiki pi ia kalakuan","Suami mancaliak jan nyo jijik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134522,"Kok sawah iyo bapamatang","Kok lading ado bintalaknyo","Hiduik nan usah mangapalang","Adab taratik nan ka dijago", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134523,"Kok siturak batang pimpiang","Tupai malopek malapari","Kok taurak jo kalingkiang","Itu banamo biopari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134524,"Kok tagak maninjau jarah","Bajalan manjapuik labo","Panghulu basipat murah","Bungka ganok Manahan cubo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134525,"Kok tagurih-gurih dalam hati","Nantikan suami sadang riang","Dalam bakucindan baru salidiki","Pakaikan kieh jan manyingguang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134526,"Kok tak dapek musim manyiang","Musim manuai den nanti juo","Kok tak dapek samaso bujang","Baranak ampek den nanti juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134527,"Kok tak kanai di ujuang karih","Kanai singanga mati juo","Kok tak jaleh corengnyo barih","Tando biti dapek juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134528,"Kok takencong jalan dari nan bana","Lindanglah ameh dek sangketo","Kaampek luruih jo saba","Indak suko marabou-rabo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134529,"Kok talantak dibaakan","Pauah di dalam padi urang","Kok taragak dibaakan","Dagang jauah di rantau urang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134530,"Kalau terpancang mau diapakan","Pauh di dalam padi orang","Kalau rindu mau diapakan","Dagang jauh di rantau orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134531,"Kok tali ka baalai juo","Antah kok pandan kamarimbo","Kok mimpi ka sampai juo","Antah kok badan tidak basuo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134532,"Kok tau barambuik panjang","Bao balimau paneh ari","Kok tau bakasiah sayang","Labiah bak makan tangah ari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134533,"Jikalau tau berambut panjang","Bawa berlimau di panas hari","Jika tahu berkasih sayang","Lebih bak makan tengah hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134534,"Kok tombuah diri barakanan","Bakabiah basamo gadang","Usahlah cando mancandokan","Saiyo makonyo sanang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134535,"Kok ujan ujankan bana","Nak kami batuduang kain","Kok anggak anggakkan bana","Nak kami cari nan lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134536,"Jika hujan hujanlah benar","Agar kami bertudung kain","Jika enggan nyatakan benar","Agar kami cari yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134537,"Kok ujan ujan bana","Nak tantu bapayuang lilin","Kok anggan anggankan bana","Nak tantu mancari lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134538,"Koki tuwa saka Semarang","Tuku apem disuwir-suwir","Aku iya kowe kok gemang","Nyata gelem ayo dipikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134539,"Kokok ayam hari pun siang","Mulai berbunyi pagi hari","Pilu nya hati adek nak pulang","Bila kah bolay berjumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134540,"Kokok ayam hari pun siang","Murai berbunyi pagi hari","Pilu nya hati adek nak pulang","Bila ka bolay berjumpa lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134541,"Kokok ayam hari pun siang","Murai berbunyi pagi hari","Pilu nya hati adek nak pulang","Bila kah bolay berjumpa lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134542,"Kokok ayam hari pun siang","Murai berbunyi pagi hari","Pilunya hati adik nak pulang","Bilakah boleh berjumpa lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134543,"Kokok ayam Megat Pandia","Kokok lalu merentas tanjung","Mimpi semalam tidur dengan dia","Peluh menjadi bunga tanjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134544,"Kokok ayam terbang ke bumi","Sayang sedikit lebih bulunya","Pokok gaharu kayunya wangi","Ditiup angin timbul baunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134545,"Kokok ayam terbang ke bumi","Sayang sedikit gugur bulunya","Pohon gaharu kayunya wangi","Ditiup angin timbul baunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134546,"Kokok ayam trebang ka bumi","Serta berkepak girai bulu nya","Pohan garu kayu yang wangi","Di tiop angin timbol bahu nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134547,"Kokok ayam turbang kabumi","Itu la biring lubih bulu nya","Pohon garu kayu yang wangi","Di tiop angin timbol bahu nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134548,"Kokok kata ayam","Kicau kata murai","Bongkok dek menganyam","Sesat mengelarai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134549,"Kolak pisang kolak serabi","Untuk hidangan kawan koleha","Setiap muslim di sunnahkan Nabi","Semasa hidup taubatlah nasuha", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134550,"Kolam baru itik tak sudu","Telaga bersih selalu ditutup","Angin menderu membawa rindu","Suara kekasih bertambah sayap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134551,"Kolam berpagar dalam jambangan","Tali terikat dari Petani","Bertambah walang kesayang sayangan","Serasa ada tuan di sini", 4, 6);
