INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134799,"Kuda terup dua sepasang","Tuntun bawa ke tengah padang","Tiada bairup tak boleh cadang","Ikut suka di lambung gelombang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134800,"Kuda tunggang pasang pelana","Tanjak dibeli belum dipakai","Rusa ditembak kijang yang kena","Hajat di hati masih tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134801,"Kuda yang mana tidak terpakay","Bawak mari pulang ke Siam","Muda yang mana tidak berkasay","Bujang yang mana dudok diam", 4, 6);
