INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134804,"Kudidi burung kudidi","Hinggap seekor ke dalam gua","Orang berbudi dibalas budi","Kasih sayang dibalas jua", 4, 6);
