INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134808,"Kudo itam putieh kukunya","Akan kudo Sutan Baraba","Adiek itam banyak cumbu-nya","Tidak bulieh bo-kata bana (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134809,"Kudo putiah makan salibu","Makan salibu padi sawah","Piliah-piliah dalam saribu","Taulan sorang dipiliah Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134810,"Kuda putih makan selibu","Makan selibu padi sawah","Pilih pilih dalam seribu","Taulan seorang dipilih Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134811,"Kudo si Alam nan ba-tuah","Singgah di kabun kalamari","Daulu sayo rang ba-buah","Kini sa-ambun tidak lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134812,"Kudu ngoyon kudu ngising","Leh ngising nan ngisor kelapa","Kudu weruh kudu nyandhing","Sing disandhing anake sapa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134813,"Kuduk-kuduk kembang lima","Kembang sedahan dipuput angin","Apa dikenang orang lama","Carilah ganti si orang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134814,"Kue apam didalam talam","Disantap bersama kopi secawan","Pepat diluar pancung didalam","Jangan didekat menjadi kawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134815,"Kue putu abang jualan","Gula merah dibungkus tepung","Dikira suci rajin sembahyang","Hidup harian 'boleh' menipu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134816,"Kue putuh abang jualan","Gula merah dibungkus tepung","Dikira suci rajin sembahyang",'Hidup harian "boleh" menipu', 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (134817,"Kue serabi kue apam","Dijual anak Bangkahulu","Adat tua menanggung ragam","Adat muda menanggung rindu", 4, 6);
