INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135172,"Lah masak padi rang Gunuang","Satangkai di gungguang alang","Barado lamo ka digungguang","Kok lapeh dapek diurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135173,"Lah masak padi rang Gunuang","Satangkai digungguang alang","Barapo lamo ka tagantuang","Kok jatuah dapek di urang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135174,"Lah masak padi rang Gunung","Setangkai digunggung elang","Berapa lama kanal tergantung","Kalau jatuh dapat dek orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135175,"Lah masak rukam manis","Tak boleh muda lagi","Lah bengkak mata menangis","Tak boleh bersua lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135176,"Lah masak rukam nan manih","Tak buliah nan mudo lai","Lah bangkak mato manangih","Tak buliah basuo lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135177,"'Lah mekar bunga selasih","Jatuh berderai sibunga laga","Kalau dikenang masa berkasih","Jatuh ke dalam air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135178,"Lah pasuak parian tuo","Aia di dalam baserakan","Hati busuak hati cilako","Aib urang suko mambukakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135179,"Lah rareh bungo limau kapeh","Duo jo bungo limau paga","Badatuih hati den malapeh","Badabok darah nan tingga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135180,"Lah runtuah tanah babingkah","Di timpo kasiak badarai","Adiak tingga jan manyumpah","Allah Taala nan mancarai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135181,"Telah runtuh tanah berbingkah","Di timpa pasir berderai","Adik tinggal jangan menyumpah","Allah Taala yang membuat cerai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135182,"Lah tabang layang layang","Barulah makan tangah sawah","Ari patang lela lah pulang","Mandeh jo tangih ateh rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135183,"Sudah terbang burung layang layang","Berulang makan ke tengah sawah","Hari sore lela pulang","Ibu menangis di atas rumah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135184,"Lah. Aku betutur niyaq beragam","Iyang mak menjunya nyaun","Anak dinakan nyaun ni intam","Pekoro ea dik dong no tilaun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135185,"Lah. Assalamualaikum unjukan ku lagi","Taq dik ku sukup no lanji","Alus basaq nu nyaun banding ni","Lekat minggo ka kau kinasi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135186,"Lah. Buaq wani mediyam kudik","Niingkoton koq tali rami","Amun kala manuk ku ea dik","Muleq engkampung pearaman kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135187,"Lah. Delah dendo luman siganding","Tengaq bangi sampai sensaung","Banga dinakan ningkoq keliling","Saksiq ti doq manuk bisaung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135188,"Lah. Ding gelama ding kedidiq","Pedagang jomo ni atur-atur","Luaq minggo ka boi teko nu dik","Sukup pandai betutur-tutur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135189,"Lah. Jemah aji ai teko no pan","Pesingga taq judah meli makanan","Berai atai gelaba derakan","Un ka lagi banga dinakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135190,"Lah. Jogo teruaq leq iting rukam","Iko bana mediyam rami","Majelis ngijin kekuri no kam","Bayaq ni no mekale jo kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135191,"Lah. Jualiq lemiyang sukup no bani","Nambar memanuk mediyam gua","Oron ku pan sukup ina ni","Dandan perantau pemua-mua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135192,"Lah. Kajang siti kajang lipatan","Kajang ku tu mediyam enges","Dagang nu siti bengen ketempatan","Dagang ku tu dagang boi bebes", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135193,"Lah. Kuri-kurian dagang piatu","Pitu kekuri diyam jembangan","Daripada mimon ea ali no sab tu","Lautan kepitu bimbang-bimbangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135194,"Lah. Lamak kiti entanjung karang","Mo berangan bekati-kati","Niyaq kebuatan oron ni tarang","Lautan kempat nangkau atai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135195,"Lah. Langit lagaq iko pekoro","Amun binanding niyaq no pan patut","Tiyo batu ni niyaq no tekiro","Tapi ngelimpasan pitu dilut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135196,"Lah. Lautan keduwo binaraq leq sanu","Tungan nelayan gorot bekunjung","Amun ea lagi tilau nu","Buweq ni darag koq bekat jantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135197,"Lah. Lemiyang beleq bengen rembayung","Ai pekepa taq sampang malau","Piyan engkau intan ni junjung","Buli no ka aku tu tilau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135198,"Lah. Mangan buas sampai merarat","Tegegaq atai enggontor jantung","Dembilaq timur ka dembilaq barat","Minggo tungan minggo ka rurung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135199,"Lah. Mangan tebu dong mangan lodo","Ea no makanan banga anak kinaq","Wahai dinakan yang alap dendo","Pulau intan tiyo ka niyaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135200,"Lah. Mekale semboon niyaq ku lupo","Rinsaq soro peranak bedekian","Tilau ku doq emas tinompo","Ai abis no ka tabik tabian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135201,"Lah. Meli selasi luman engkadai","Akan bino taq lat yunan","Terimo kasi no yang alap atai","Iyan oron kampung alaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135202,"Lah. Memanuk eling jataq lensana","Melayu enggang taq sama sungang","Wahai kawan bijak laksana","Kekuri no kau dong kam bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135203,"Lah. Messi payi niyaq pandai ngari","Kuleq dikauq ea pan asi","Dandan enggai koq metenaq diri","Mungkin dandan noq kinasi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135204,"Lah. Mo inti mo pan rambutan","Luaq taq pahang puq mentakap","Sila siti silalah intan","Dagang yang ina sediyo ngejawap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135205,"Lah. Mopoq badu diyam beledik","Mo sabun banga dengkati","Bana sigoq eling nu ea dik","Pitu kam dinakan kekuri kiti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135206,"Lah. Mopoq badu diyam beledik","Supaya lingau dong jo lambat","Amun pan bana baraq nu dik","Alap saksiq ti sampai ni tamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135207,"Lah. Naanan pedi betiru koq dilau","Pesingga aku merumaq sanu","Balik-balik aku tu tilau","Un ka lagi iyang mak nu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135208,"Lah. Nangkau atai lautan kempat","Tungan nelayan ngendoq empalam","Amun niyaq salaq pendapat","Lautan kelimo rindu samalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135209,"Lah. Ngembang mekar sesumping melur","Teruaq panas layu pelele","Bintang pengaraq koq bintang timur","Kekuri no kam kami mekale", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135210,"Lah. Ngetek-ngetek banga biduk kinaq","Ngudik enderio waktu sesubu","Amun niyaq lupo dagang yang ina","Buweq ni biru mengusik kalbu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135211,"Lah. Nginum kupiq mediyam sawan","Taguan ku doq gula-gula batu","Bana-bana eling nu kawan","Susa memiya jomo begitu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135212,"Lah. Nginum kupiq taq kadai nyonya","Sambil ngendaq orop empantai","Pad-pad elum menjunya","Pitu no kam mesanang atai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135213,"Lah. Nompo suang ai pan dembuan","Susa nompo mun niyaq siganti","Mimang pendikar mimang andalan","Nepak kam tangan reramai kiti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135214,"Lah. Puq kiti ensedi pantai","Tiyo bana taq pantai nani","Sebelum sambung ku lagu rangkai","Piyan pikiran nu siti jerani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135215,"Lah. Puyut gipun alap ro ni","Puyan jomo diyam jembangan","Niat kami yang sebana ni","Niman pantun dikauq endangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135216,"Lah. Rindak-rindak olos ni jarang","Pekayan balu ngebit pianu","Teturan dik ku sukup no tarang","Patut no pan alap atai nu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135217,"Lah. Sambil kekuri taq telaq buan","Bintang telaq baraq ku batu","Dik ku sasat entengaq lan","Piyan uleq nu boi teko pitu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135218,"Lah. Sayang-sayang buaq rambutan","Dong sinundaq dong ginuyang","Amun dik ku niyaq kebuatan","Siyan oron dik ku sayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135219,"Lah. Sepu empat pernomo buan ni","Iko jomo merumaq apan","Gunda atai ebok-ebok oron ni","Guyaq ni oyo mokoq susa pan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135220,"Lah. Siti jerani oron masuhur","Siyan maraq nyaun banding ni","Sempakak mekar tomoq ni subur","Un no ka jomo ampun ni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135221,"Lah. Suang abai ungus ni lumat","Ni ogo leq jomo bani niyaq bani","Betaq lat ea taq moto barat","Dandam birahi ea no oron ni", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135222,"Lah. Sumping kemboja enjataq mijaq","Buat periasan moto ti puas","Teko ku tu dik niyaq boi sinajaq","Boi bo beriu sukup no daras", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135223,"Lah. Sumping ni ngembang bedagang rugi","Niyaq bege tantu niyaq laku","Dong kau ngenduleq entedo lagi","Un pekoro akan tilau ku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135224,"Lah. Sumping selasi tomoq diyam liyang","Layu sumping ni teruaq elau","Amun bege eling nu sayang","Maku ijin aku tu tilau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135225,"Lah. Telaq buan bintang ni oyo","Lemiyang memanuk oron ni enggang","Andalan dilut pendikar derio","Ai no sitemu diyam gelanggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135226,"Lah. Wahai siti yang alap budi","Berai atai ku kau jo ni intam","Lautan keenam kenangan abadi","Mokoq kusut mediyam dom", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135227,"Lahia jo batin nak santoso","Nan jan tumbuah hino jo malu","Habisi agak di mulonyo","Musiah jo bandiang mak jan lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135228,"Lai babaua dalam padi","Bak apo tidak dilampaikan","Lai bakaua dalam ati","Bak apo tidak disampaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135229,"Lai di-timbo nan bak udang","Tidak karuntuang dalam rimbo","Lai di cinto nan bak urang","Tidak ka untuang apo dayo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135230,"Lai di-timbo nan bak udang","Tidak karuntuang ba-a juo","Lai di-cinto nan bak urang","Tidak ka-untuang ba-a juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135231,"Lai ditimbo nan bak udang","Biluluah juo nan katimbo","Lai di cubo nan bak urang","Nan buruak juo nan ta suo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135232,"Lai-koh siko Sutan Nau","Si-rauik di-ateh kasau","Lai-koh siko balam nan mau","Kami ba-mukasuik nak man-cakau (Pantun Minangkabau)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135233,"Lain balang lain isinya","Isi tumpah semua terbuang","Lain orang lain hatinya","Hati menyalah dihina orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135234,"Lain batang lain buahnya","Buah masam disambal orang","Lain orang lain lidahnya","Lidah tajam dibedal orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135235,"Lain cerindak lain ditudung","Sayang rambutan tidak berbuah","Lain kehendak lain di lansung","Memang badan ku tidak bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135236,"Lain hulu lain parang","Orang bekerja naik ke bukit","Lain dahulu lain sekarang","Jauh berbeza bukan sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135237,"Lain ikat lain simpainya","Lain tiang lain pemasak","Lain adat lain memakainya","Lain orang lain kehendak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135238,"Lain kembang lain tangkainya","Tangkai lapuk bungapun gugur","Lain orang lain perangainya","Perangai buruk namapun kotor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135239,"Lain ketupat lainlah lepat","Jadi juadah amat termashyur","Lain ku jerat lain yang dapat","Nasi pun sudah menjadi bubur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135240,"Lain lubuk lain ikannya","Ikan banyak dipukat orang","Lain duduk lain makannya","Makan tamak diumpat orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135241,"Lain lubuk lain ikannya","Lain padang lain belalang","Lain ku pujuk lain yang kena","Hati bimbang bukan kepalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135242,"Lain padang lain belalang","Lain kolam lain ikannya","Ada hukum boleh berjalan","Ada negeri lain adatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135243,"Lain padang lain belalang","Lain lubuk lain ikannya","Hidup merantau dimana tergalang","Sopan santun jaga harkatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135244,"Lain padang lain rumputnya","Rumput panjang padang kan semak","Lain orang lain mulutnya","Mulut lancang orang kan muak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135245,"Lain pahat lain bajinya","Lain baji lainlah simpai","Lain adat lain kajinya","Lain kaji lainlah curai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135246,"Lain pedang lain hulubalang","Pahlawan perkasa kerisnya tajam","Lain padang lain belalang","Cita rasa nan berencam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135247,"Lain pelekat lain warnanya","Lain belang lainlah ragi","Lain adat lain maknanya","Lain orang lainlah budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135248,"Lain perahu lain pencalang","Hendak bertolak perahu sarat","Lain kutuju lain yang datang","Hendak mengelak hatiku berat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135249,"Lain pompat lain sukatnya","Lain sukat berbeda berat","Lain adat lain sifatnya","Lain sifat berbeda niat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135250,"Lain selat lain sungainya","Lain padang lain belalang","Lain adat lain perangainya","Lain orang lain pengenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135251,"Lain tilan lain sembilang","Orang membeli di pasar murah","Lain ku pesan lain yang datang","Kepalang mandi biarlah basah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135252,"Lain ulu lain parang","Sayang greja di atas bukit","Lain dulu lain skarang","Jaoh bayda bukan sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135253,"Lain ulu lain parang","Sayang greja di atas bukit","Lain dulu lain skarang","Jaoh bayda bukan sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135254,"Lain ulu lain pemarang","Bergantungan buah sulasih","Lain dahulu lain sekarang","Berlainan hati yang kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135255,"Lainlah mumbang lainlah kelapa","Mahu disayur manakan dapat","Lain ditunang lain yang tiba","Mau mundur sudah terlambat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135256,"Laju ajong terkikis-kikis","Si ambang-ambang si bunga kayu","Nombor satu mister Inggeris","Nombor dua cikgu Melayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135257,"Laju bas lalu terbabas","Jatuh terkulai si dahlan rambai","Libas melibas projek dikebas","Cuai lalai kerja terabai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135258,"Laju laju perahu laju","Blayar manuju kasurubaya","Biarla lupa kain dan baju","Jangan la lupa kepada saya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135259,"Laju laju perahu laju","Laju sampai tengah samudra","Peluk cium terasa syahdu","Sampai merinding si bulu roma", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135260,"Laju laju perahu laju","Laju sekali ke Surabaya","Boleh lupa kain dan baju","Janganlah lupa kepada saya", 4, 6);
