INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135529,"Lalo pesaiar aning sunda","Pesiame ngadu motor","Ape gune selapuq harta benda","Lamun begawean ajari koruptor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135530,"Lalo pesiar aning labuan","Telalo ngeang sepede","Dendeq girang nipu dengan","Karena sino perbuatan lenge", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135531,"Lalo pesiar aning Labuhan Kayangan","Leq tono bau kedit","Lamun side penasaran","Leq embe ita bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135532,"Lalo pesiar jok Senggigi","Dendeq lupaq te jauq topat","Dendeq te girang pade korupsi","Sengaq sino amanat rakyat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135533,"Lalo pesiar leq karang Baru","Lueq gati buaq cermin","Perhatiang nasib bapak guru","Hidupna masih endeq terjamin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135534,"Lalo pesiar tipaq Surabaya","Ndaq lupaq jauq takilan","Dendeq gati nyobak ganja","Lama ndeq mele ketagian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135535,"Lalo pesta ojok Ampenan","Kawin kebaya dait songket","Apa guna jadi presiden","Laguq ndeq tao pimpin rakyat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135536,"Lalo sekolah bilang jelo","Eleq lengan lueq saq nyapa'","Timaqne kodeq kanaq sino","Laguq sembahyang ndeqna lupa'", 4, 6);
