INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135538,"Lalo sekolah jauq tas","Lalo meken jauq bakaq","Imen pejabat leq atas","Imen rakyat kodeq leq bawaq", 4, 6);
