INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135543,"Lalok bakalang jo pusako","Ajaran guru tak buliah lapeh","Sungguh pun batang lai mardeso","Ingek-i pucuak ka taampeh", 4, 6);
