INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135592,"Lalulah mati si burung geroda","Panah kesaktian Sang Rajuna","Demikianlah juga rasa adinda","Hampirkan datang suatu bencana", 4, 6);
