INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135595,"Lama dikurung si burung pucung","Riang kenari terbang tinggi","Kenapa murung bibir muncung","Mari menari sampai ke pagi!", 4, 6);
