INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135600,"Lama pendeta pergi bertapa","Kebun semak semakin luas","Dapat sehasta hendak sedepa","Si tamak tak pernah puas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135601,"Lama suda gopong di rajok","Angkot di junjong sampie sapuloh","Saya saumpama apong buranyot","Untong tusangkot dia hujong pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135602,"Lama sudah gopong di rajut","Angkut di junjung sampai sepuluh","Saya umpama ampong berhanyut","Untung tersangkut di hujung buluh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135603,"Lama sudah merendam baju","Panas terik baru dijemur","Lama sudah kupendam rindu","Sayang adik tak mau menegur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135604,"Lama sudah orang memukat","Dari dulu hingga sekarang","Lama sudah abang terpikat","Memberi tahu rasanya gamang", 4, 6);
