INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135702,"Lamun tipak bulan puase","Sembahyang teraweh harus te gaweq","Lamun araq perintah siq kuase","Selapuqna harus tegaweng", 4, 6);
