INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135710,"Lamur te bedait isiq raksese","Nyerek isiq pade byline","Perpisahan isiq pad aniline","Agen deq teparan anaq durhede", 4, 6);
