INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135718,"Lancang kuning belayar malam","Aying deras karangan tajam","Jikalau tiada malim yang faham","Di situ banyak kapal tenggelam", 4, 6);
