INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135753,"Lang terbang ka kebun china","Membuang penat diatas charang","Panday tuan ka mana mana","Kutika sesat akal pun kurang", 4, 6);
