INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135760,"Langgam nan tujuah dikaji pulo","Dalam kalarasan Koto Piliang","Basa ampek Balai mulo partamo","Tadiri dari ampek urang", 4, 6);
