INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135766,"Langit berawan di bulan purnama","Puteri kayangan membaca hikayat","Budi tuan saya terima","Jadi kenangan sepanjang hayat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135767,"Langit berawan gunung berjajar","Udara nyaman bertiup segar","Ilmu tuan tidak diajar","Bagai kemenyan tidak dibakar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135768,"Langit berawan indahnya buana","Hijau rumput menutup bumi","Hadir tuan secantik bunga","Menambah seri majlis kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135769,"Langit berkelikir","Bumi bertemberang","Salah-salah fikir","Jadi teman orang", 4, 6);
