INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135771,"Langit bertemu awan","Jarum bertemu benang","Riang bertemu kawan","Senang hatiku senang!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135772,"Langit bintang beruang besar","Pedoman ke arah utara","Hasrat menjadi orang besar","Kejar bila ada kesempatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135773,"Langit biru awan berkapas","Menunggu senja kain dijerumat","Rumah baru kereta dirampas","Banyak belanja tidak berhemat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135774,"Langit biru awan kelabu","Matahari sinarnya merah","Beta bingung di dalam kalbu","Kekasih hilang tak tentu arah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135775,"Langit biru cuaca redup","Orang berkelah dengan bekalnya","Harta itu manisan hidup","Ingatkan Allah nikmat madunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135776,"Langit biru dihembus bayu","Dari gunung sampai segara","Pantun ini pantun Melayu","Junjung tinggi Rukunegara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135777,"Langit biru sino maraq ruane aseq","Nyeryo'ang umbak saq bereri bejaoq","Aten sei ndeq gen aseq","Ngelepas batur ojok negeri jaoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135778,"Langit biru tidak berawan","Burung helang terbang sekawan","Pendaki Malaysia semangat pahlawan","Kemuncak Everest dapat ditawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135779,"Langit biru udara terang","Awan bertumpok berangkap-rangkap","Jikalau malu dihadapan orang","Main mata ganti berchakap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135780,"Langit bumi laut dan gunung","Berbatas dengan negeri tetangga","Harta waris dari leluhur","Kita jaga kita kembangkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135781,"Langit cerah awan membiru","Angin bertiup menyejuk kalbu","Kalau 'ndak tahu di untungku","Ibarat kaca terhempas ke batu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135782,"Langit cerah cuaca terang","Kota bersinar terang benderang","Ombak memukul ka batu karang","Merayu hati dagang sa orang", 4, 6);
