INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135786,"Langit kelabu tanda nak hujan","Air ditadah tumpah ke tanah","Dengan ilmu bijak membezakan","Kenal bidaah tahu sunnah", 4, 6);
