INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (135788,"Langit mendung angin menderu","Ribut kencang hujan gerimis","Berat sungguh menanggung rindu","Hati terkenang terus menangis", 4, 6);
