INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136145,"Lekaq-lekaq lekan segane","Dendeq lupaq jauq montor","Adeng-adeng jauq dedare","Sengaq lueq dengan koruptor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136146,"Lekaq-lekaq leq kelem Jum'at","Bedeit kence Pak Muji","Dendeq girang keang kelembi ketat","Agen dendeq te sapaq isiq dengan keji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136147,"Lekaq-lekaq leq kelem Jum'at","Tipaq mushola gitaq dengan ngaji","Pasti te sikse leq akherat","Lamun leq dunie girang keji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136148,"Lekaq-lekaq leq kenjelo","Sompiq lalu timpek batur siq biase","Berpisah ndeq asek bejelo-jelo","Sengak wah tentuang Siq Kuase", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136149,"Lekaq-lekaq leq sedin kubur","Sambilne mete lolon bawang","Lamunne mete seneng leq kubur","Pacu-pacu ngaji sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136150,"Lekaq-lekaq leq sedin pesisi","Selun-selun araq gansing meripit","Mule uwah jati tradisi","Ta bepisah pasti ta bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136151,"Lekaq-lekaq leq sedin reban","Onyak-onyak araq ular bebise","Lamun dating bulan Ramadhan","Endaq lupaq pada bedo'e", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136152,"Lekaq-lekaq leq sedin runung","Araq montor berlanggaran","Lamunne mele idup seneng","Pacu-pacu begawean", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136153,"Lekaq-lekaq leq tengak kubur","Gite cewek meruan leaq","Lamun side aneng Singapur","Awak kurus angen side kakak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136154,"Lekaq-lekaq meta bubur","Mele bubur laguq mauq sejadah","Lamunda mele tenang leq dalem kubur","Rajin-rajin siq da pada beribadah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136155,"Lekaq-lekaq meta kembang cempaka","Meta kembang sino laguq mauq jagung","Lamunna araq dengan kena bencana","Becat-becat siq da pada betulung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136156,"Lekaq-lekaq ngadu sepeda","Ngadu sepeda jari lelah","Sida mauq tama surga","Sengaq sida rajin beribadah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136157,"Lekaq-lekaq ojok Kebon Talo","Gitaq dengan saq jeneng-jeneng","Wah dua taon kakak meranto","Surat-surat endarang dateng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136158,"Lekaq-lekaq ojok kokoq","Kokoq saq terteananim kokok telabah","Endaq pada aseq laloq","Lamun lemaq tepade bepisah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136159,"Lekaq-lekaq ojok kubur","Bedait kanca biwir abang","Te bebaturan endaq sampai takabbur","Lamun te bepisah pade saling ingetang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136160,"Lekaq-lekaq ojok labuan","Lalo gitaq perau becadik","Lamun kakak mele bekelampan","Mdak sampe lupaq leq adik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136161,"Lekaq-lekaq ojok Payau","Gitaq kanak maen layang","Munde payu lalo merantau","Ndaq payu ngaji sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136162,"Lekaq-lekaq ojok peken lepak","Beli kotak isine kapur","Ndendeq girang muni lekak","Lamunte mele lueq batur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136163,"Lekaq-lekaq ojok taliwang","Leman Sumbawa Besar taeq jaran","Lamunda mele bedait kanca tiang","Tulis juluq surat perjanjian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136164,"Lekaq-lekaq ojok taman","Dendeq lupaq ojok bangket","Timaq side jaoq leq negeri dengan","Laguq side tetep ku inget", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136165,"Lekaq-lekaq ojoq Yogyakarta","Ndak lupaq aning keraton","Lamun side ngalai desa ojoq kuta","Lamun lupaq kanca semeton", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136166,"Lekaq-lekaq sedin bangket","Ndak lupaq nyeduh mi","Lamuda mele pade selamet","Pade girang lalo silaturrahmi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136167,"Lekaq-lekaq taeq jaran","Lekaq-lekaq keliling bere","Dendeq kaken dowen dengan","Lamune endeq mele sengsare", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136168,"Lekaq-lekaq timpe' Aikmel","Dendeq lupaq lekan Pesanggerahanne","Lamun te bepisah dendeq sampe nangis begumel","Sengaq selapuqne wah ndaraq kenene", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136169,"Lekaq-lekaq timpe'Aikmel","Dendeq lupaq lekan Pesanggerahanne","Lamun te bepisah dendeq sampe nangis begumel","Sengaq selapuqne wah ndaraq kenene", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136170,"Lekaq-lekaq timpeq SB","Tangket cewek begandengan","Lamun te tangket batur te ndeq berabe","Agen laun siq bedait taoq te bebarengan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136171,"Lekaq-lekaq tipak kubur","Bedait sik biwir abang","Bebatuan endakte kabur","Tebejau'an tesaling ingetan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136172,"Lekaq-lekaq tipaq Afrika","Tepu juluk leq Suriah","Lamun de mele tama surga","Harus de rajin beramal jariah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136173,"Lekaq-lekaq tipaq balen pelinggih","Lalo nyinggaq palu kanca kikir","Lamun araq kepeng lebih","Endaq lupaq eleq fakir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136174,"Lekaq-lekaq tipaq Jepang","Dendeq lupaq beli apokat","Rajin-rajin isiq sembahyang","Agenda selamat leq akherat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136175,"Lekaq-lekaq tipaq kios Papuq Gerih","Pada lalo beli vitsin","Dengan suguh seleke sugih","Dengan fakir seleke miskin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136176,"Lekaq-lekaq tipaq Narmada","Leq tengaq langan mauq kepeng","Dendeq nangis sida denda","Sengaq tiang uwah dating", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136177,"Lekaslah besar anakku ini","Boleh ku serah ke tempat mengaji","Menghadap guru lebai dan haji","Di akhirat kelak tidaklah rugi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136178,"Lekek-lekek ojok sekolah","Dende lupe jauk jangkrik","Uwah suwe te bepisah","Nane te bedait malik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136179,"Lekeq-lekeq bau peku","Peku te bau jari oat saraf","Sengek entah tene pantun si piekku","Kututup ia sik kete maaf", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136180,"Lekeq-lekeq beli tebu","Tebu te beli leq balen siri","Lamun bi mele bedait kence eku","Endeq lupek jauk ruti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136181,"Lekuq-lekuq taek jaran","Aning peken beli tepung","Lamun girang baca Qur'an","Leq dalem kubur pasti tenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136182,"Lelahku mete laq Mami","Ndeq ku toaq ye aning Sandiq","Dendeq bae side asal muni","Sengeq elaq tajeman lengan ladiq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136183,"Lelaki perempuan leka menari","Dalam temasya di Pekan Kuah","Kalau tuan bijak bestari","Apa ubatnya orang rasuah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136184,"Leleah sino taoq baling","Bale sino bale loq Rinal","Uwah lekan laeq bi telang","Jangka pas te bedait endeq te saling kenal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136185,"Lelene mati dithutuk","Gawa mrene tak-sujenane","Yen rene ra nate pethuk","Ndang mrene tak-entenane", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136186,"Lelong suasa di Jalan Gaya","Rasa terbetik hendak berhenti","Bunga di desa banyak durinya","Jika memetik berhati-hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136187,"Lemah gemalai dayang menari","Menghibur raja dan ratu","Akrab mempelai suami isteri","Tanda suka dapat menantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136188,"Lemah lembut dara jelita","Juga seorang yang kuat beriman","Sopan santun budaya kita","Menjadi kebanggaan zaman-berzaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136189,"Lemah lembut pandan bedurik","Putus tali penyerat pagar","Lemah lembut membawa dirik","Putus ati kamek menanggar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136190,"Lemahlah kaki anak siamang","Luka parah ditusuk ranting","Lemahlah sendi menengok tunang","Mata merah misai melenting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136191,"Lemahlah kaki anak siamang","Luka parah ditusuk ranting","Lemahlah sendi menengok tunang","Mata merah misai melenting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136192,"Lemahlah kaki anak siamang","Lutut sakit makan tak mau","Lemahlah sendi menengok tunang","Perut buncit badan berbulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136193,"Lemahlah kaki anak siamang","Lutut sakit makan tak mau","Lemahlah sendi menengok tunang","Perut buncit badan berbulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136194,"Lemah-lembut tulus mesra","Hairan! takut bukannya suatu","Dihambat lari berempera","Tetapi bukannnya syaitan dan hantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136195,"Lemak bersusu roti perata","Banyak khasiat makan capati","Tuan membisu seribu kata","Mimik muka cermin hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136196,"Lemak cendol santan kelapa","Harum sirap si kayu manis","Lurus bendul tidak mengapa","Jangan berharap pada si bengis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136197,"Lemak dangai kuih bergula","Tepung pulut si kacang merah","Pajang sungai bertemu kuala","Panjang mulut cerita tak sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136198,"Lemak halus sutera cindai","Kalau dilipat Cuma sekepal","Lembu lurus orangnya pandai","Cekap cepat hartanya sekapal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136199,"Lemak kuih bercampur badam","Buah kundur dibuat halwa","Api asmara hujan tak padam","Cinta semarak di dalam jiwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136200,"Lemak manis buah bidara","Sayang selaseh saya lurut kan","Buang Mak buang saudara","Kerna kaseh saya turut kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136201,"Lemak manis chondong kajkeling","Sudah ku keeling ka jawa pula","Hitam manis dudok menjeling","Sudah menjeling tetawa pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136202,"Lemak pulut intinya daging","Manis dadih si gula tebu","Selicin belut seligat gasin","Pandai berdalih bijak menipu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136203,"Lemak santan diambil pati","Keras matan si pokok tua","Semak ingatan tuan menyepi","Penuh muatan nakhoda tiada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136204,"Lemak suji kelapa ke tepi","Suji panas cepat laku","Indah janji dalam mimpi","Jambatan emas sungainya susu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136205,"Lemak sungguh buah berangan","Goreng dulu sebelum dimakan","Rajin usaha membawa kesenangan","Ada wang buat kebajikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136206,"Lemak tidak manis pun tidak","Bila terbuang dimakan semut","Beremak tidak beribu pun tidak","Kalau terbuang siapa nak pungut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136207,"Lemak tidak manis pun tidak","Gula melaka dari seberang","Beremak tidak beribu tidak","Kerana jasa dikenang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136208,"Lemak-lemak si buah pinang","Lemak lagi pulut berinti","Emak ayah datang meminang","Dapat sudah si buah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136209,"Lemaknya nasi bergulai udang","Lemak berbau santan kelapa","Remak kumati berputih tulang","Tidak kumahu berputih mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136210,"Lemaq laloq mbeli bubur","Sendeq man lekaq lalo ngarat","Sai mele benar leq kubur","Pacu-pacu nabung jari akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136211,"Lemaq-kelemaq lekaq-lekaq","Lekaq-lekaq bareng tiya","Manusia niki lueq besiyaq","Lueq besiyaq leq harta dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136212,"Lemaq-lemaq aning gawah","Ndek lupaq jauq adik","Uah setaon te pisah","Jelo niki te bedait malik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136213,"Lemaq-lemaq aning peken","Beli jangan jari penyampah","Bakat biasa bau te owatin","Laguq bakat ate sekat ne solah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136214,"Lemaq-lemaq aning sekolah","Kadu tangkong kadu songket","Timaqta pada berpisah","Leq dalem angen tetap tiang ingat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136215,"Lemaq-lemaq bau kenyamen","Jangka lupaq apa jariq penyampah","Berembe entan endeq pinaq angen","Empat taon gin bepisah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136216,"Lemaq-lemaq jangan pindang","Ndeqman tekaken juluan teriq","Malen jameq ku lalo midang","Maraq jemaq tebilin meradiq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136217,"Lemaq-lemaq lalo meken","Beli buaq aji seribu","Ndeq ku bau nahen angen","Ndeq bedait timaq seminggu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136218,"Lemaq-lemaq lalo meken","Dendeq lupaq beli sekoteng","Ndek ne salaq angenku demen","Sengaq gitaq side siq wah dateng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136219,"Lemaq-lemaq pinaq pager","Jari pager barena gajah","Sai-sai mele pinter","Pacu-pacu isiq berajah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136220,"Lemaq-lemaq tae dokar","Endaq lupaq bejarupan","Pacu-pacu pada belajar","Agen berguna leq masa depan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136221,"Lemaq-lemaq te nyampah","Mbeli urap-urap leq Inaq Uyun","Enteh ite berajah","Kadu jari bekal eraq/leq laun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136222,"Lemaq-lemaq tepada ngasa","Ndak lupaq bale ta periri","Mun sida lalo ngalai desa","Ndaq lupaq kadang jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136223,"Lemari kotak-kotak deket kolangsah","Leq atas ne poster-poster merek bagus epe","Lamun de berintak ndeq te bepisah","Berarti ide ingkarin Tuhan siq lebih kuasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136224,"Lemau manis chondong ka keling","Sudah ka keeling ka jiwa pula","Hitam manis du lok menjeling","Sudah menjeling tertawa pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136225,"Lemau prot batang berduri","Amek daon-nya mengulam ketupat","Jangan-lah tuan kasana-kemari","Mari-lah jiwa disini-nya tempat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136226,"Lemau prot batang berduri","Champor stanggi iris daon-nya","Rindu tak makan sa-malam sa-hari","Mari-lah tuan disini tempat-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136227,"Lembah dalam jangan di harong","Kalau direnda mendapat cedera","Kalau kekanda sa-ekor burong","Kanda terbang dapatkan dinda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136228,"Lembah Danum tempat kunjungan","Bersama dengan Pulau Sipadan","Banyak senyum menjadi sanjungan","Senyum jangan tidak sepontan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136229,"Lembah ini lembah paduka","Di Tempasok bantungan merdeka","Hari ini kita bersuka","Entah esok kita berduka (Kumpulan Pantun Sabah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136230,"Lembah kampong di tepi kuala","Kuala kecik tak banyak ikan","Sudah tulong bagi kerja","Di dalam setahun dua belaih bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136231,"Lembah kinta lembah bermutu","Kapal korek terlalu chantek","Sebab manusia tabi'at bagitu","Takut borek anak-nya rintek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136232,"Lembayung senja di Kampus Bangi","Suasana indah begitu terpesona","Hati terkenang budi mewangi","Tetap dibawa ke mana-mana", 4, 6);
