INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136957,"Makan gelagah sambil meninggir","Minum air di dalam gelas","Kalau disedekahi bukanku fakir","Diberi budi indaku terbalas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136958,"Makan ikan banyak tulangnya","Ikan beli dari nelayan","Tanya tanya mana rumahnya","Boleh singgah biar semalam", 4, 6);
