INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (136960,"Makan jerok jerok sesawi","Ayam selasay di tepi gua","Handak buno buno skali","Biar lah kasay mati berdua", 4, 6);
