INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137855,"Masak santan buah kelapa","Dapat nona mata keranjang","Ampas kering minyak di wajan","Sinyo ganteng jadi sasaran", 4, 6);
