INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137857,"Masak sebiji buah betek","Belah-belah tak ada biji","Tengok di luaq rupa cantek","Tengok di dalam lebeh lagi", 4, 6);
