INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137859,"Masak sebiji buah pinang","Merah marak di hujong dahan","Kasehkan abang Fiza timangkan","Habeh bulan beganti bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137860,"Masak sebuah pisang lidi","Masak sebuah duo buah","Adolah upayo kondak ati monti","Ondak bocakap sepatah duo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137861,"Masak sebutir dibelah-belah","Arang dan kain dibasakan","Sudah takdir kehendak Allah","Orang lain jangan disalahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137862,"Masak sekawah buburnya cair","Buah sukun mari dicencang","Bagaikan sawah harapkan air","Hujan tak turun kemarau memanjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137863,"Masak sotong si-ayer mendideh","Di-champor mi dengan lazat-nya","Tegoh di-sokong rebah di-tindeh","Itu-lah resmi adat manusia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137864,"Masak susu lengang-lengang","Putik belimbing terjetu-jetu;","Katup pintu renggang-renggang","Petik dinding buka pintu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137865,"Masak tauchu ikan selangat","Champor halia champor garam","Sudah tahu api hangat","Kenapa di-choba hendak genggam?", 4, 6);
