INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137868,"Masak tauge goreng lada","Lada ditumbuk lada putih","Tuan menagih pada yang ada","Saya merajuk pada yang kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137869,"Masak tauhu masak berampai","Puchok geti di-champor juga","Kalau ku tahu kaseh ta' sampai","Rela ku mati dari mula-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137870,"Masak terong gulai berempah","Hendak menjamu orang daik","Emak beruntong bapa bertuah","Munkin-kah anak menjadi baik?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137871,"Masak tertutup tunggu mendidih","Ikan terubuk juga cencaru","Sudah kututup riwayat nan pedih","Kini kubuka hidup yang baru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137872,"Masak udang bergulai lemak","Jadi jamuan datok jeragan","Makanan terhidang bagitu banyak","Habis-kah tuan lima pinggan?", 4, 6);
