INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137877,"Masaklah buah kayu tulang","Makanan anak barau-barau","Kalau untung kembali pulang","Kalau miang hilang di rantau", 4, 6);
