INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (137888,"Masalah jangan lama dipikirin","Waktu terbuang kurang kerjaan","Jangan harapan orang lain","Persoalan sendiri selesaikan", 4, 6);
