INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139453,"Mulialah kerja yang berfaedah","Mengikuti syarak menuruti adat","Bertuah manusia yang beribadah","Hatinya warak pekerti terhormat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139454,"Mulo ditabang batuang kini","Rabah tasanda ka pamatang","Mulo dikarang surek kini","Rindu dandam bukan kapalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139455,"Mulanya ditebang betung ini","Rebah tersandar ke pematang","Mula dikarang surat ini","Rindu dendam bukan kepalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139456,"Mulo ku angon djeroh pedi murip","Nguk mugegerdip puren achire","Nge beta bange nenong nasib","Sajang ni garib tubuh selangke", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139457,"Mulo partamo nagari asa","Iyolah Pariangan Padang Panjang","Batambah kambang manusia","Dicancang latiah kayu ditabang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139458,"Mulonyo banda diilaikan","Nak buliah ka jalan aia","Basa andiko didirikan","Ka payuang panji ateh dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139459,"Mulung hutan sepepah","Mulung kampung datuk","Angkat tangan sembah","Minta ampun datuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139460,"Mulung sepelepah","Mulung kampong datuk","Angkat tangan sembah","Minta ampun datuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139461,"Mulut kecil perut besar","Buntal beracun tidak dimakan","Mendidik anak jangan berkasar","Pujuk nasihat ibu amalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139462,"Mulut laju dari otak","Negara jiran habis dibentak","Lagu yang diajari habis dimintak","Dengan sombong dan juga bongkak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139463,"Mulut sakit malu berkata","Kaki kejang duduk di tilam","Perut buncit susu berjela","Mandi telanjang Berukpun demam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139464,"Mulut sakit malu berkata","Kaki kejang duduk di tilam","Perut buncit susu berjela","Mandi telanjang berukpun demam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139465,"Mun bebele sedin aiq","Jarum benang jari penjait","Pemomot tiang jangka lelaiq","Jangka ngimpi tiang bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139466,"Mun bedait kanca amaq tari","Ndaq lupaq besalaman","Memang sulit meta pegawean nani","Senaq lueq deangan KKN", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139467,"Mun belauq mun bedaye","Ndaq lupaq jauq takilan","Mun meq mele solah aranme","Kenaq-kenaq entan perentah dengan tiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139468,"Mun de lampaq ngadu sedan","Dedeq lupaq lengan kiri","Mun de mule mele KKN","Gen de tame aning bui", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139469,"Mun de mele tepu leq bale","Dendeq lupaq tindok leq dipan","Mun de mele jari pegawe","Dendeq lupeq kepeng sogokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139470,"Mun kempait nyelem nyeliti","Maraq kisap tame ceraken","Mun bedait dalem impi","Maraq idap jari penganten", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139471,"Mun kenjelo lilin teriq","Bangket taoq te nujak","Mun de lalo bilin ariq","Sanget laloq isiqde lupaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139472,"Mun ku bau gedeng manggis","Pelecing manuk jari kandong","Mun ku inget siq ku nangis","Dendeq bilin aku sue laloq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139473,"Mun te mangan dadeq besalo","Miaq beberoq dendeq terong doang","Relagang tiang lalo","Sengaq tebepisah sementara doang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139474,"Mun tejari dengan lapoh","Dendeq paran diriq te gagah","Siq ku paran gen te bejodoh","Sulun-sulun jak te bepisah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139475,"Mun tengkoar nyontok leq lingkok","Lio bawah sak gitak aik","Endek kuah sak mene lalok","Tindok nyalak mangan ndek maik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139476,"Muncul tercegat rembulanb permai","Di tabir mega sinar kencana","Hatta sejagat terasa damai","Bumi dijaga etika sempurna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139477,"Muncung tajam bagai tombak","Itulah senjata si ikan todak","Jalan becak sudah ditambak","Muka jerawat cantik berbedak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139478,"Mundar-mandir keliling kota","Jalan bertembung orang berkuda","Bodoh Pak Pandir ada cerita","Bodoh sombong mendabik dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139479,"Mungkin karajo dimakan patuik","Lansailah hutang dek babayia","Malah nan bana nan dituruik","Baru manyarah ka takadia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139480,"Munku inem oat saq pait","Lilin ketoloq leq dalem aiq","Munku inget siq tebedeit","Tabilin lalo ndekne maiq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139481,"Munku mele beli paoq","Yaq kumele aning Terare","Side lalo aning jaoq","Ate ariq terasa sengsare", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139482,"Munku nginem jengke keselit","Lilit ku terik timpak aik","Lamun de inget sik te bedait","Tebilin menarik endekne maik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139483,"Munme' lalo aneng pante kute","Dende' lupe' beli buluan Bali","Munme' lalo aneng teme surge","Dende' lupe' sembahyang mengaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139484,"Munsoh negah enggau nyawa","Sida nebah nyawa pemadu iya.",'"Tu Ngumbang seduai Kana!','Ngelaban perang bebunoh nyawa!" (Pantun Sarawak) (Ngayau Mepi) (Unggat Kedu) (Pantun Ngayau Mepi. Borneo Literature Bureau.) (Tahun 1964)', 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139485,"Munte milu pertandingan","Ndaq lupaq nyabut undi","Pacu-pacu nerimaq pelajaran","Jari bekal lemaq mudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139486,"Mupakatlah Datuak suri Dirajo","Katumanggungan jo Parapatiah","Musuah dilawan jo bicaro","Atah jo bareh mak nyo basisiah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139487,"Mura di bli terubok tadi","Mari di chelop asam rembenia","Orang idop lawan nya mati","Harta dunia tingal di dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139488,"Mura di bli trubok tadi","Mari di chelop asam remenia","Orang hidup lawan nya mati","Harta dunia tinggal di dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139489,"Mura sekali terubok tadi","Panjang chelop asam remenia","Kita hidop lawan mati","Orang hidop perasan dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139490,"Murabok burong palembang","Di tiop angin sulatan daya","Mas perak bolih di timbang","Budi orang apa kandaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139491,"Murah dibeli terubok tadi","Panggang dichelop asam rumnia","Kita hidop lawan nya mati","Jadi orang perasa'an dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139492,"Murah rasaki dek sakato","Basangketo mangkonyo caia","Haromat kapado ibu bapo","Sarato ka urang mulia-mulia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139493,"Murah rezeki golongan petani","Perlu hanya rajin bekerja","Berkembang pula dakyah Nasrani","Sekolah mission mereka mereta rata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139494,"Murai berkicau di pagi hari","Berkicau terus tanpa henti","Pandai berbual cantik berbudi","Sampai masa elok berhenti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139495,"Murai berkichau di-pagi hari","Hinggap mari di-pohon kelapa","Memegang kerbau dengan tali","Perihal manusia bagaimana pula?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139496,"Murai kenari riuh berkicau","Hinggap seekor di atas dahan","Apabil diri jauh di rantau","Kenangan desa yang kau tinggalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139497,"Murai-murai mandi","Mandi atas rumput","Murai mula menyanyi","Mari kawan ikut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139498,"Murak mas burong di wata","Turbang mulangsing di kaki awan","Gunong mas tamba pumata","Bua ati gurangan tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139499,"Murid patuh murid yang rajin","Cari waktu kerja sendiri","Kaya ilmu sukar dicari","Kaya harta bisa sehari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139500,"Murtabak dijual di gerai di kedai","Bubuh inti goreng di kuali","Maruah bangsa usah digadai","Jauh sekali dijual beli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139501,"Musang jabat di atas daun","Bertiti di ranting ranting pata","Brapa lebat hujan s'tahun","Jatoh ke-tana haram tak basa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139502,"Musang jebat berkemas kemas","Lepas di kandang menyambar ayam","Suka perchaya tukang mas","Tentu sa-bongkal makan sa-mayam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139503,"Musang jebat di atas daun","Meniti di ranting tiada patah","Berapa lebat hujan setahun","Jatuh ke bumi haram tak basah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139504,"Musang jebat memanjat akaq","Burong kedidi puteh dadanya","Ujan lebat gunong tebakaq","Embun setitek padam apinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139505,"Musang jebat memanjat akaq","Burong kedidi puteh dadanya","Ujan lebat gunong tebakaq","Embun setitek padam apinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139506,"Musang jebat terlari lari","Panjat klapa di tepi pagar","Sama berjebat bersuri suri","Serta di tanya apa kabar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139507,"Musang jubat di atas dahon","Maniti di ranting ranting pata","Brapa lubat ujian satahon","Jatoh kabumi aram takbasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139508,"Musang pandan di tebing tinggi","Mati ditembak baginda bungsu","Janganlah dikenang dirinya orang","Diri kita belumlah tentu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139509,"Musang pandan di tobiang saborang","Mati ditembak dek gindo bonsu","Janganlah dikonang diri orang","Diri awak bolunlah tontu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139510,"Musang pandang di tobiang saborang","Mati ditembak dek gindo bonsu","Janganlah dikonang diri orang","Diri awak bolunlah tontu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139511,"Musang tua hendak meniti","Hendak meniti batang berangan","Alang kechewa baik lah mati","Supaya tidak panjang kenangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139512,"Musim bunga sakura mekar","Hawa dingin menjadi hangat","Dalam hidup segala yang sukar","Menjadi kayu pembakar semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139513,"Musim dingin pemandangan berubah","Segala menjadi segar dan hijau","Kalau hidup menemu susah","Hanya padamu aku mengimbau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139514,"Musim haji orang ke Mekah","Amalan baik sambil bertaubat","Dalam hati abang bersumpah","Menanti adik sampai kiamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139515,"Musim hujan banyak cendawan","Boleh digulai dengan santan","Cantik sungguh gadis rupawan","Bolehkah kita berkenalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139516,"Musim hujan banyak cendawan","Burung belibis datang mengais","Dunia lelucon untuk tertawa","Cerita sedih untuk menangis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139517,"Musim hujan musim bertani","Padi subur rumpun berkembang","Antara jasmani dengan rohani","Pemenuhan haknya harus seimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139518,"Musim ini musim menugal","Padi ditugal si dangan kayu","Kekanda pergi adinda tinggal","Sama-sama menanggung rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139519,"Musim ini musim menugal","Padi ditugal si dengan kayu","Kakanda pergi adinda tinggal","Sama-sama menanggung rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139520,"Musim lecah ke ujung kampung","Cari sayur rebus cicah","Patan tiang 'ku ikat sambung","Biar penat berpeluh basah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139521,"Musim luruh daun pun gugur","Daun muda hijau di ranting","Hancur luluh jiwa tersungkur","Bunga dipuja sudah disunting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139522,"Musim masak buah durian","Kina Melayu banyak membeli","Lepas sudah mara kapunan","Buaya tidak menungap lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139523,"Musim panen pohon berbuah","Pekerja menghitung upah","Lelaki merasa tua","Wanita menuai dalam rupa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139524,"Musim tanam mudim mataq","Pongkaq beras jari dulang","Kulalo juluq inaq","Ikhlas dait do'anda kuharepang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139525,"Musim tengkujuh di tengah hari","Kapal berlabuh di Tanjung Jati","Kita bertukar cincin di jari","Kalau hilang badan pengganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139526,"Musim tengkujuh di tengah hari","Kapal berlabuh di Tanjung Jati","Kita bertukar cincin di jari","Kalau hilang badan pengganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139527,"Musim timoq ujan pun turon","Nama ujan kapak kanji","Oghang betanya siapa yang panton","Kami menjawab kami sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139528,"Musim ujan bau nyale","Dedare bajang pade berangen","Enget side pade saq leq bale","Momot mesak endek karuan angen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139529,"Musim ujan lueq temberena","Sempiq tokol ngaken empit","Bilang jelo kulekan tene","Nggak nangka ta bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139530,"Musim ujian nyet idapne","Sengaq simbut endeq naraq","Masi tiang inget ruane","Timaq bedait araq semendaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139531,"Muslim Hindu Kristen Katolik","Kita hanya insan di bumi","Tuhan tidak jauh di langit","Kita sembah dan kita puji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139532,"Mustika embun di gunung Sari","Putik pauh di dalam dulang","Tiga tahun adinda cari","Abang jauh di negeri orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139533,"Musuah nan indak dicari-cari","Basuo pantang mailak","Syarak lah nyato babuhua mati","Kalau adat babuhua sentak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139534,"Musuh bukan sebarang musuh","Musuh kerana dendam kesumat","Rusuh bukan sebarang rusuh","Rusuh kerana abang berangkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139535,"Musuh garang lembing dihunjam","Bisanya keris Taming Sari","Asah parang berkilat tajam","Kenapa menghiris jari sendiri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139536,"Musuh melanggar masuk istana","Tentera lari baris memanjang","Membertih istinggar di daksina","Penghuni utara kenapa kejang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139537,"Musuh melanggar pekan pesara","Membawa belantan berhulu panjang","Terdengar istinggar dari utara","Penghuni selatan kenapa kejang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139538,"Mutiara putih intan bertatah","Secantik kaca tetap suram","Akal memilih beras dan antah","Iman memisah halal dan haram", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139539,"Na landit ma na porkot","Bulung dongdong gabe parada","Na hantjit ma na ngotngot","Na dung adong djadi suwada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139540,"Na so torang do marsulu","Padaohon mai tu parmaraan","Adop dongan na dua tolu","Mambaen na tupa di tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139541,"Na suongkon danon ni bulu","Tu aek so dipangan gulaen","Tu darat so ra tubu","Madabu so dong topotan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139542,"Nabbas umme si dengan parrang","Hati lah hati tekannak batang","Uddah tahu juddi di larang","Harte habis telantang-lantang", 4, 6);
