INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139724,"Naik turun membawa parang","Untuk menebas semak belukar","Adik sepantun bunga dikarang","Membuat cemas dada berdebar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139725,"Naik turun membawa teman","Nampaknya senang wajah berseri","Adik sepantun bunga di tanah","Banyaklah kumbang datang menyeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139726,"Naik turun mencari mumbang","Mumbang kelapa nyiur hijau","Adik sepantun dewi membang","Hilang dimata hatiku risau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139727,"Naik turun mencari rotan","Masuk hutan rimba belantara","Adik sepantun dewi kayangan","Cantik rupawan tiada bertara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139728,"Naik turun orang ke tepian","Pergi mandi petang dan pagi","Adik sepantun kembang pilihan","Sampai mati abang menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139729,"Naik turun orang mendaki","Payah kaki penat anggota","Adik sepantun delima sakti","Merah pipimu membuat gila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139730,"Naik turun rasanya nafas","Letih mendaki anak tangga","Adik sepantun bunga kapas","Petih suci tiada bernoda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139731,"Naiklah orang ke kubu Belanda","Tempat ia bermain baris","Barulah senang kalbu kekanda","Laksana gambar sudah ditulis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139732,"Naindung Siti Fatimah","Naindung akan di bidadari","Miski jauh Mekah Madinah","Hati kasih jangan unduri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139733,"Naindung Siti Fatimah","Si untung berjalan tengah jambatan","Mispun jauh Mekah Madinah","Sudah untung suratan badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139734,"Nak balik baliklah kacang","Batang padi ditebang jangan","Hendak balik baliklah abang","Bendul tepi dilangkahi jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139735,"Nak beli buah setoi","Duit tak dak tukaq teloq","Nak cari oghang betoi","Kerja sendiri jangan baloq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139736,"Nak berkebun berkebun sangat","Buah limau masak layu","Nak berpantun berpantun sangat","Macam Cina masuk Melayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139737,"Nak carai antakan surek","Nak kawin tando batimbalan","Adapun banamo alek ketek","Alek nan ketek dipasangkuikkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139738,"Nak ditobang tidak tetobang","Ditobe jugokan jadinya","Nak diompang tidak teompang","Dilopean jugo kan jadinyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139739,"Nak dulang kubeli dulang","Dulang berisi antah bertih","Nak pulang kuhantar pulang","Mari kuhantar dengan gajah putih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139740,"Nak dulang kubeli dulang","Dulang berisi pucuk sungai","Nak pulang kuhantar pulang","Mari kuhantarkan ke pucuk mahligai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139741,"Nak dulang kubeli dulang","Dulang berisi susu mentah","Indung nak pulang kuhantar pulang","Mari kuhantar dengan pantun sepatah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139742,"Nak hujan hujan sakalik","Sanang kitak bakajang kain","Nak buang buang sakalik","Sanang kitak bacari lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139743,"Nak ilia ka Indogiri","Singgah sabanta ka lading panjang","Di mano adat mulo bandiri","Iyo di Pariangan Padang panjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139744,"Nak makan tidak berikan","Ikan dulu dimakan juga","Hendak berkayuh tidak bersampan","Hati rindu ditahan juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139745,"Nak mandi mari lah mandi","Satu timba mandi berdua","Hendakk mati mari lah mati","Satu kubor kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139746,"Nak mandi marilah mandi","'Setimba kita berdua","'Nak mati marilah mati","Sekubur kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139747,"Nak samo tampak putiah hati","Tandonyo janiah tak balunau","Bajawek tangan malah kini","Baitu adat Minangkabau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139748,"Nak tahu punai malayang","Di banto turun ka padi","Nak tau di mulo sayang","Di mato turun ka ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139749,"Hendak tahu punai melayang","Dari banto turun ke padi","Ingin tahu mulanya sayang","Dari mata turun ke hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139750,"Nak tajuk tajuklah puan","Ramunggai batang berduri","Nak masuk masuklah tuan","Timbanglah cukai adat negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139751,"Nak urang di kampuang pulai","Singgah lalu kapalo koto","Di mano kusuik namuah salasai","Ujuang jo pangka tak basuo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139752,"Nak urang Koto Hilalang","Nak lalu ka pakan Baso","Malu jo sopan kalaulah hilang","Habihlah raso jo pareso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139753,"Nakhoda kapal mengejar lanun","Lanun bersarang di Pulau Maluku","Saya umpama si pandai tenun","Menenun menganyam corak hidupku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139754,"Nakoda bernama sali","Tanam padi tengok jugak","Mintak dokwa jangan mati","Kalu tak mati bertemu jugak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139755,"Nakoda bernama sali","Tanam padi tengok jugak","Mintak dokwa jangan mati","Kalu tak mati bertemu jugak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139756,"Nalet dadar leq sedin telaga","Meta ampel kadu minaq layang","Lamunda mele tama sorga","Ndaq lupaq pada sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139757,"Nalet puntiq telu lolo","Endaq lupaq baca ayat Quran","Timaq da bilin tiang lalo","Endeq tiang gen bertangisan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139758,"Nama aslinya Sunda Kelapa","Diubah menjadi Batavia","Lalu Jayakarta","Sekarang nama kerennya Jakarta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139759,"Nama buloh itu","Gelaq buloh tok-tok","Ada satu kayu","Untok buat katok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139760,"Nama pendeta tertulis di tugu","Baik jasanya tetap dikenang","Putik cinta sabar ditunggu","Inikan pula sudah bertunang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139761,"Nama semalu daun semalu","Daun kuncup disentuh jari","Berbagai rentak irama lagu","Disiarkan untuk menghibur hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139762,"Namanya harum di seluruh dunia","Kedengaran bila dilaungkan azan","Terus berdakwah terhadap manusia","Menyelamatkan mereka di hari kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139763,"Namanya Raden Jaya Perbata","Umurnya baharu remaja sedang","Rindu dendam tiada menderita","Selama adinda tiada dipandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139764,"Nampak anggun si burung geroda","Mengejar jentayu bersama merak","Redap di unggun tak mahu reda","Dipuput bayu semakin marak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139765,"Nampak api Tanjung Tuan","Kuala Linggi tanahnya merah","Besar hati mengharapkan tuan","Langit yang tinggi kusangka rendah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139766,"Nampak bendera terkibar-kibar","Nampak dari atas bukit","Hati tak boleh tahan sabar","Kalau digagah jadi penyakit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139767,"Nampak beralur ombak gelombang","Mercik ke batu tunggulnya karang","Bunga melur tak sempat kembang","Habis digetu dek kuku orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139768,"Nampak berbusa diolak sampan","Air di tasik bertanah rendah","Bila berpuasa elok bersopan","Kenapa asyik meludah-ludah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139769,"Nampak bercahaya keris pusaka","Hanya sebilah di tengah sawah","Dirgahayu Malaysia tanah airku","Negara bertuah rakyat bermaruah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139770,"Nampak bergoyang julai meranti","Cuaca redup di pantai permai","Suami disayang seikhlas hati","Agar hidup rukun dan damai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139771,"Nampak berjeram burung kedidi","Terbang melantun arah ke desa","Lumba haram untuk berjudi","Ghairah penonton tumpang berdosa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139772,"Nampak berkilau cahaya mutiara","Bersamam gelang di dalam dulang","Waktu merantau hasrat membara","Usahlah pulang mengendong walang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139773,"Nampak berlabor tongkang China","Champak saoh anak kelasi","Nak sahot takot tak kena","Itu sabab diam kan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139774,"Nampak berpijar tirai berapun","Damar jelaga tinggalnya puntung","Tak mahu belajar bahasa Jepun","Lalu berniaga mencari untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139775,"Nampak berseri pohonnya duku","Daun-daunan di hujung julai","Saban hari bencana berlaku","Pengguna jalanan selalu lalai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139776,"Nampak berseri teruna jejaka","Segak beraksi memakai dasi","Dalam melayari bahtera merdeka","Nakhoda kelasi mesti serasi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139777,"Nampak bersinar bagaikan emas","Gadis cantik pakai kebaya","Sekali mendengar rasa tak puas","Hati berbisik itulah dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139778,"Nampak bersinar bintang nan merlit","Kerdip pancaran jauh di angkasa","Perkata yang benar mekipun sulit","Terima teguran seikhlas rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139779,"Nampak bersinar permata bujur","Hiasan butang kebaya kelabu","Kalau benar abang nan jujur","Adik bertatang di piala kalbu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139780,"Nampak bersulam mata parang","Memotong nangka di hujung titi","Puas merantau negeri orang","Di sini juga tambatan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139781,"Nampak bertandan gugusan kelapa","Kelapa muda pohonnya rendah","Memang padan orang yang alpa","Apa yang ada lesaplah sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139782,"Nampak bertandan gugusan kelapa","Kelapa muda pohonnya rendah","Memang padan orang yang alpa","Apa yang ada lesaplah sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139783,"Nampak bulan sedang mengambang","Cahaya kencana sungguhnya cantik","Hajat rombongan kami pertimbang","Bunga mana ingin dipetik?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139784,"Nampak bulat di sangka lemo","Sayang sa-bijik di paytak kangkong","Tuan pakay kulit arimo","Handak gertak rusa sa-kampong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139785,"Nampak bulat di sangka lemo","Sayang sa-bijit di paytak kangkong","Tuan pakay kulit arimo","Hendak gretak rusa sa kampong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139786,"Nampak dari Selat Melaka","Alun gelora gumpal-gumpalan","Cuba hindari pesona petaka","Sepakat mesra menambah amalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139787,"Nampak dari tanah Makkah","Di sana ramai anak bidadari","Sabarlah kita sebelum nikah","Sama-samalah menjaga diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139788,"Nampak indah saujana mata","Melaut tanaman harta pusaka","Banyak faedah berdarmawisata","Pungut pengalaman semasa bersuka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139789,"Nampak kapal berlayar laju","Kapal belayar hala ke Judah","Kalau malas membasuh baju","Kotor dan comot nampak terdedah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139790,"Nampak kecut buah peria","Bersama kunyit dalam belanga","Gila memecut di jalan raya","Bagai monyet meronyeh bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139791,"Nampak kemas Laksamana Bentan","Hendak pergi tanah seberang","Walau emas bertatahkan intan","Perak bercelup jangan dibuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139792,"Nampak lee koh jala ditebar","Ikan dibawa sepenuh tong","Saham jatuh jantung berdebar","Bagai nyawa di tali gantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139793,"Nampak manis di tangan puteri","Gelang emas permata delima","Jalan jauh keliling negeri","Kernbara bermula langkah pertama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139794,"Nampak merah sebela timor","Anak si Ah Leng menjual susu","Mintak Allah panjang kan umor","Bolay jaga anak chuchu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139795,"Nampak merata rumpunan pandan","Di tanah pamah akar berceragah","Sebagai wanita idola teladan","Jasmani tak lemah rohani gagah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139796,"Nampak perahu terapung-apung","Cik Rasib tukang kemudi","Jangan selalu harapkan untung","Kalau tak nasib mana nak jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139797,"Nampak rnerah tapi tak pedas","Sos tomata campur gula","Badan sihat minda cerdas","Ada wawasan ada hala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139798,"Nampak sejajar pohonnya getah","Ditanam melebar jarak selari","Ada pelajar degil membantah","Biadab mencabar guru sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139799,"Nampak setara pohonan cempa","Dilindung mega ketika rembang","Diasuh mesra ibu dan bapa","Harumi keluarga masih berkembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139800,"Nampak sudah layang-layangan","Sayang sulangat di atas tirai","Nampak sudah kasih dan sayang","Sampai semangat tidak bercerai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139801,"Nampak suram itulah kaca","Berlian muda tetap bercahaya","Mata fakir mencari punca","Habis cara menjana upaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139802,"Nampak terserlah cahaya di kuala","Masjid berkubah indah sekali","Berkat Bismillah kalam bermula","Menghadap sembah ke bawah duli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139803,"Nampak tertib teruna jejaka","Leka sembahyang tepi penjuru","Apalah nasib pantun pusaka","Ibarat lelayang putus teraju?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139804,"Nampaknya kembang si bunga melati","Hendak dibawa ke Singapura","Memanglah abang merasa hati","Abang tak sangka adik berpura", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139805,"Nampaknya merah tepian paya","Kiranya lada dipetik orang","Jangan marah kepada saya","Pahala anda takut berkurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139806,"Namun hati tak lah bernasib","Semata-mata kerana Allah Taala","Maka berzakatlah tanpa nisab","Niscaya amal akan penuh cahaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139807,"Namun janganlah antum cemas","Allah tak pernah menzalimi hambaNya","Bila taubat terbalut oleh emas","Maka siksa ditukar dengan senyumNya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139808,"Nan ampek di hilia pulo namonyo","Pasia Situmbuak Sumaniak jo Salo","Duo di kapalo: Pasia Laweh jo Koto Tuo","Duo di ikua: Koto Panjang jo Suruaso (Pantun Minangkabau; Sungai Tarab Nagari Tuo) (N.M. Rangkoto) (Pantun Adat Minangkabau. Departemen Pendidikan dan Kebudayaan. Jakarta: Proyek Penerbitan Buku Sastra Indonesia dan Daerah) (Tahun 1982)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139809,"Nan ampek jinih barek tugehnyo","Ditiliak dari sagalo bidang","Sabarih tak buliah lupo","Sanitiak tak buliah hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139810,"Nan ampek jinih di dalam rapek","Mampakatokan cupak jo gantang","Kalau siang iyo dipatungkek","Jokok malam iyo dipakalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139811,"Nan asa banamo gombak","Sabuah lai banamo bauak","Urang baduo badunsanak","Ujuang gala untuak nan bauak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139812,"Nan babarieh nan bapahek","Nan baukua nan bakabuang","Curiang barieh buliah diliek","Cupak panuah gantang babubuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139813,"Nan babarih nan bapaek","Nan bacupak nan bagantang","Tuahnyo kato samupakaek","Cilako dek kato nan basilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139814,"Nan baiak cinto di hati","Ka nan elok tunggang niatan","Nafsu dikungkuang aka budi","Pandang ka dunia baukuran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139815,"Nan basasok bajurami","Nan bapadam bapakuburan","Soko pusaka kalau tasalami","Mambawa cayo di inggiran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139816,"Nan binguang suruah sarayo","Nan cadiak bawo barundiang","Kalau talatak di tampeknyo","Indak nan bulek ka basandiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139817,"Nan buto paambuih lasuang","Nan pakak palapeh badia","Usah dikicuah urang nan binguang","Tunjuak ajai kalau nan pandia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139818,"Nan dirasokan raso","Diuji dek pareso","Kasaimbangan raso jo pareso","Partimbangan aka itu namonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139819,"Nan elok baiak ditalangkai","Rancak dibuang kalau buruak","Dangki mandangki usah dipakai","Kawan sairiang sago dituhuak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139820,"Nan elok samo dipakai","Nan buruak samo dibuang","Hiduik di dunia samo pandai","Silang salisiah tak diadang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139821,"Nan galah tetaplah galah","Nan akar tetaplah akar","Nan salah tetaplah salah","Nan benar tetaplah benar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139822,"Nan gawa juo nan manyasa","Nan mahilangkan juo nan ka mangganti","Itulah hukum nan asa","Partiapan suku dalam nagari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139823,"Patah tumbuah hilang baganti","Pusako turun ka nan mudo","Manuruik biaso dalam nagari","Mancari hukum tigo parkaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139824,"Nan kaampek manjadi hutang","Anak kamanakan dipaliharo","Lahia jo batin inyo dibimbiang","Salamat awa sampai akianyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139825,"Nan kaampek pulo disabuik","Urang nan suko baramah-ramah","Budi haluih tak barek muluik","Bakato salalu ambiak bawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139826,"Nan kaduo banamo Rajo Adat","Batampat di balai Caniago","Baliau mangatua adat istiadat","Punyo ustano pulo di Buo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139827,"Nan kalimo tando urang baraka","Tamasuak kapado nan lahia juo","Baama saliah sarato tawakka","Baiak jo buruak dipasisiahkannyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139828,"Nan kanduang indak ka pangganti","Batali darah pun indak pulo","Gala nan baru buliah dicari","Kalau pusako duo tigo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139829,"Nan kasalapan martabat 'budi'","Urang jauhari jo bijaksano","Jokok nan baiak iyolah budi","Kalau nan indah iyolah baso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139830,"Nan kasambilan martabat 'parhimpunan'","Pusek jalo pumpunan ikan","Martabat panghulu nan sambilan","Handaklah basuo dalam amalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139831,"Nan katigo manjadi hutang","Iyolah mamaliharo harato pusako","Kalau ketek dipagadang","Kok buruak dibaikinyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139832,"Nan katigo undang-undang luhak","Rantau barajo luhak bapanghulu","Ka lawik rajo nan punyo hak","Di darek panghulu mamacik hulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139833,"Nan kuek paangkuik baban","Nan bagak pahadang musuah","Tatap nan bana dikamukokan","Kalau nan ukan dibuang jauah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139834,"Nan kuriah iyola budi","Nan merah iyolah sago","Nan baiek iyolah budi","Nah endah iyolah baso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139835,"Nan kuriak iyolah kundi","Nan merah iyolah sago","Nan baiek iyolah budi","Nan endan iyolah baso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139836,"Nan kuriek iyolah kundi","Nan sirah iyolah sago","Nan baiek iyolah budi","Nan indah iyolah baso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139837,"Nan labiah malu jo sopan","Dapek diganti kok nan hilang","Bia takucak minum jo makan","Asa babaia sagalo hutang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139838,"Nan laki-laki banamo Sutan Balun","Duo jo adiaknyo Mambang Sutan","Abih bulan baganti tahun","Anak batigo lah gadang-gadang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139839,"Nan mancancang juo nan mambari pampeh","Bautang iyo babayaran","Nan mambunuah juo mambari baleh","Bapiutang iyo batarimokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139840,"Nan ma-nolong Tuan Panasa","Handalanga di gudang kopi","Kudian mangko ka ma-nyasa","Adiek kanduang ma-ubah janji (Pantun Minangkabau) (A.L van Hasslet) (Midden Sumatra: Leiden Brill) (Tahun 1881)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139841,"Nan padusi Puti Indo Julita","Laki-laki bagala Datuak Suri Dirajo","Putipun kawin jo Sang Sapurba","Sutan Paduko Basa gala anaknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139842,"Nan pandai bakeh baguru","Nan tuo tampek batanyo","Manampuah pamatang nan baliku","Nan sanang disusahkannyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139843,"Nan pandeka lawan basilek","Nan tahu bakeh batanyo","Kalau talatak alah ditampek","Di sinan adia makonyo tibo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139844,"Nan patuik samo sakanduang","Samo taraso tantang itu","Tandonyo budi leh baanjuang","Manjago hino jo malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139845,"Nan rajo tagak di barieh","Kok penghulu tagak di undang","Bukan murah basisik karieh","Kapalang tangguang pusako lindieng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139846,"Nan salah nyato tabanam","Nan bana nyato kan timbua","Bijaksano tahu dirasam","Candokio pandai malulua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139847,"Nan sedikit di dalam hati","Nan terbakar di dalam lidah","Nan cerdik berhati-hati","Nan sabar menahan ulah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139848,"Nan tuo baramal saliah","Amal ibadat nan tapakaikan","Padi masak jaguang maupiah","Rangkiang baririk di halaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139849,"Nanas ada di dalam kubun","Dulang datang deri patani","Siang bupanas malam burumbun","Tidak blas mumandang kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139850,"Nanas Babagon sudah tersohor","Terkenal enak di seluruh Sabah","Jangan berlakon pura-pura luhur","Hati tidak dengan muhibah (Kumpulan Pantun Sabah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139851,"Nanas datang dari sebrang","Bijan berchampor benay kladi","Ku sangka ujan sampay petang","Tak tau ujan sa-krat hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139852,"Nanas datang dari sebrang","Bijian berchampor benay kladi","Ku sangka ujan sampay petang","Tak tau ujan sekrat hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139853,"Nanas dijual di pasar niaga","Ramai orang datang membeli","Emas perak perhiasan dunia","Budi bahasa perhiasan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139854,"Nanas masak rupa nya merah","Tarok mari dalam bakol","Tuan mas sahya tembagah","Bolay kan kita berchampoh ga'ol", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139855,"Nanas moris nanas Sarawak","Banyak ditanam di negeri Johor","Jangan ketawa jangan berlawak","Guru mengajar ikhlas jujur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139856,"Nanas serumpun tidak berduri","Buahnya manis seperti gula","Panas setahun hujan sehari","Telaga yang kering penuh semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139857,"Nanas serumpun tidak berduri","Rasanya manis seperti gula","Panas setahun hujan sehari","Telaga yang kering penuh semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139858,"Nangis bebeaq nyugulan aiq mata","Baturan bueq gedeq sampiqna bekemiq","Bepisah ndeqna araq senengna","Ate sepaq mangan ndeq maiq (Pantun Sasak)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139859,"Nangka cempedak buah di hutan","Masak sebiji di pangkal dahan","Lutong sorak kera tak makan","Tangkap wap timpa perasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139860,"Nangka dipanjat tidak berbunga","Buahnya layu gugur bersarang","Dengan tiada suatu guna","Buat malu dipandang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139861,"Nangka dipanjat tidak berbunga","Buahnya layu gugur bersarang","Dengan tidak suatu guna","Buat malu dipandang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139862,"Nangka hilang getah","Durian hilang duri","Gagap hilang petah","Rasuah hilang peribadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139863,"Nangka muda digulai lemak","Buah keranji masak tersangkut","Harta dunia jangan ditamak","Bila mati tidak mengikut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139864,"Nangka muda digulai lemak","Buah keranji masak tersangkut","Harta dunia jangan ditamak","Bila mati tidak mengikut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139865,"Nangka rnasak usah disimpan","Baunya harum ke serata desa","Anak rnuda tunggak harapan","Membina negara membela bangsa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139866,"Nangka yang masak jangan di tindih","Biji yang tua boleh di tanam","Siksa Tuhan yang sangat pedih","Namanya tempatnya neraka jahannam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139867,"Nangkodoh ingek di kamudi","Padoman usah dilupokan","Adapun pakaian di nagari","Limo syaratnyo rang katokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139868,"Nanti kan surut menyalam tiram","Ayer pasang pasang berbani","Terpasang suda kandil di dalam","Trang sebagi bintang ordani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139869,"Nanti surut menyalam tiram","Ayer pasang pasang berbani","Sudah terpasang kandil di dalam","Trang sebagi bintang orani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139870,"Nanti surut menyalam tiram","Ayer-pasang pasang berbani","Sudah terpasang kandil di dalam","Trang sebagi bintang orani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139871,"Nanti surut menyalam tiram","Ayer-pasang pasang berbani","Sudah terpasang kandil di dalam","Trang sebagi bintang orani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139872,"Nasi' aru' gulai keladi","Ndak kanna' sarru' tang ade paggi","Tak tahu adat dalam negeri","Tak akan pandai membawa diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139873,"Nasi basi atas para","Para baru nasi pelimau","Pucat kasih badan sengsara","Hidup segan mati tak mahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139874,"Nasi basi atas para","Para dibuat para baharu","Putus kasih badan sengsara","Hidup segan mati tak mahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139875,"Nasi beriani bawa berbuka","Dagang puasa lama di perbatasan","Emosi harmoni serta peka","Bertimbang rasa sesama insan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139876,"Nasi beringin penuh seloyang","Makan bersama adik dan abang","Tiada angin pokok bergoyang","Lain ditebang lain yang tumbang!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139877,"Nasi bungkus ikan kering","Dimakan orang sambil berenang","Jangan putus biar genting","Biar tinggal sehelai benang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139878,"Nasi bungkus nasi kering","Di makan sampai Berenang","Jangan putus biar genting","Biar tinggal sehelai benang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139879,"Nasi bungkus nasi kering","Dimakan sampai Beranang","Jangan putus biar genting","Biar tinggal sehelai benang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139880,"Nasi digado telur dadar","Minum seguer disadap nira","Kalau selalu mau benar","Anda tak bisa bergembira", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139881,"Nasi dimasak menjadi bubur","Bubur jangan dicampur nila","Hati rosak baik dihibur","Tidak dihibur menjadi gila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139882,"Nasi dingin bersayur mumbang","Sayur dimasak dalam belanga","Kami tak ingin melihat kumbang","Kalau kumbang merosak bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139883,"Nasi dingin di dalam panci","Jangan dicampur dengan sagu","Kita bercinta di dalam hati","Tidak ada orang yang tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139884,"Nasi dingin di dalam panci","Digoreng baru makan","Kita bercinta di dalam hati","Makripat saja yang menyampaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139885,"Nasi dingin di dalam panci","Beras pulut dibuat ketan","Kita bercinta di dalam hati","Entah kapan kesampaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139886,"Nasi ditanak bekal memburu","Memburu denak sampai ke hulu","Sampul biru surat pun biru","Membawa pesan hati yang rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139887,"Nasi ditanak kok badatuih","Alah dimakan mako taraso","Rakyat nan banyak kok tak tauruih","Indak sah manjadi rajo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139888,"Nasi ditanak masaklah sudah","Sudah masak padamkan apinya","Harumkan hidup dengan ibadah","Agar tenang menjalaninya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139889,"Nasi goring si minyak sapi","Makan berulang rasa tak jelak","Jauh panggang dari api","Lamaran tuan sudah ditolak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139890,"Nasi hangit di dalam kole","Sayur meranti ulam kerabu","Tinggi melangit kejayaan diperoleh","Jagalah hati ayah dan ibu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139891,"Nasi himpit sayur keladi","Santan cair si rending Minang","Bagai pipit tak kenal padi","Sampan di air kenapa berenang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139892,"Nasi hurhe manukna","Unang teal bureranna","Masi ajar boruna","Unang suda napuranna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139893,"Masing-masing panggil ayamnya","Jangan menonjol temboloknya","Masing-masing ajar putrinya","Jangan habis sirihnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139894,"Nasi kebuli padang seberang","Rumput manis makan dek rusa","Kami tak peduli pada orang","Lemak manis orang tak rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139895,"Nasi kuning semangkuk sabun","Mari jamu Raja China","Putih kuning bunga di kebun","Kembang sekuntum orang yang punya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139896,"Nasi kuning semangkuk sabun","Mari jamu Raja Cina","Putih kuning bunga di kebun","Kembang sekuntum orang yang punya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139897,"Nasi kunyit apa ikan-nya","Ikan selangin dari Betawi","Sakit hati apa obat-nya","Choba tersenyum bawa menyanyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139898,"Nasi kunyit dagingnya rusa","Untuk bekalan ke Lembah Danum","Walau sedikit tinggi nilainya","Budi kalian bagaikan album (Kumpulan Pantun Sabah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139899,"Nasi kunyit panggang ayam","Ketiga dengan pulut inti","Bumi senget dunia karam","Belum dapat belum berhenti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139900,"Nasi lemak berbungkus daun","Mengalas perut paginya hari","Mari kakak berlawan pantun","Berbalas sambut seorang sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (139901,"Nasi lemak bergulai keladi","Di-bayar satu di-ambil dua","Kenapa banyak sudah terjadi","Kapal satu nakhoda dua?", 4, 6);
