INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140258,"Ojok bangket jauq tambah","Ojok peken beli sate","Timaq ite uwah bepisah","Laguq kamu tetap leq ate", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140259,"Ojok bangket lalo mangan","Gitaq dengan kaken nangka","Ndaq girang ngolok dengan","Lamun teolok sakit atena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140260,"Ojok bangket ngadu selana","Ponggoq tambah lekan besi","Dengan nane lueq raosna","Raos weq ndeqna araq berisi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140261,"Ojok bangket ngobok baling","Jari gawe kanaq repok","Timagna segqre jari lalang","Mun ta jodoh pasti jaq ta besopoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140262,"Ojok gawah nembak kijang","Gitaq dengan maling rotan","Dendeq jari dengan sombong","Sengeq dengan ndeqna demen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140263,"Ojok kebon bau lembaong","Pinaq jangan gedeng nangka","Jari pejabat ndendeq sombong","Jaman nengke rakyat merdeka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140264,"Ojok kebon bau paku","Uleq-uleq tebait isiq dengan","Lamun side tetu mele lek aku","Bareh te bedait elek langan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140265,"Ojok kebun bau paku","Uleg-uleg isiq dengan","Lamun side tetu mele leq aku","Bereh te bedait leq langan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140266,"Ojok kebun nembak kedit","Sampe dohor ndaraq terik","Siwaq bulan endeqk tewah bedeit","Nengke de dateng langsung ngendeng merarik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140267,"Ojok kios beli sandel jepit","Selaloq endah beli sie","Nangke malik te bedait","Uwah de siq sue leq Malaysie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140268,"Ojok Masbagek beli paok","Ndak lupaq beli rombengan","Kabar ksksk ndek tiang taok","Ndek wah bedait selam sebulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140269,"Ojok Masbagik te lampaq","Ojok Palestina te lalo ngaret","Durhaka leq inaq amaq","Nerake nganteh leq akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140270,"Ojok opotik beli antis","Onyaq-onyaq kedantuk loang","Ndaq sedih ndaq nangis","Tiang lalo setaun doang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140271,"Ojok Pancor beli terang bulan","Onyak-onyak araq siluman","Lamun wah mauq pengawean","Ndak lupaq kampung halaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140272,"Ojok pantan selembor angen","Gitaq karang saq beleq-beleq","Side sekeq siq tiang kangen","Satie tiang antih jangke oleq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140273,"Ojok peken baru beli terasi","Ajin terasi mahan leq bilan puasa","Negarante sine Negara demakrasi","Ndeqne pejabat doang epen kuasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140274,"Ojok peken beli impan","Impan empak empak layah","Antih tiang lalo bekelampan","Bekelampan pete nafkah (Pantun Sasak)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140275,"Ojok peken beli kacamata","Ojok bangket bau kedit","Uwah legan laeq te saling peta","Beruq nengke te saling dait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140276,"Ojok peken beli kedit","Keditne kedit becik","Baruq sejelo te bedait","Nengke owah bi ngendeng merarik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140277,"Ojok peken beli kedit","Keditne kedit becik","Baruq sejelo te bedait","Nengke owah bi ngendeng merarik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140278,"Ojok peken beli manggis","Ndaq lupaq beli sia","Bilang jelo tiang nangis","Inget diriq tebilin ojok Malaysia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140279,"Ojok peken beli manggis","Tolong leq bakaq bedereq-dereq","Dengan dereq pada nangis","Dengan sugeh rereq-rereq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140280,"Ojok peken beli perie","Perie pait perie pesugulan","Lamun mule side setie","Antih tiang kareng enem bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140281,"Ojok peken beli sebek","Adeng-adeng leq tengeq langan","Lamun ndeq mele te pedeq","Dendeq girang ngejek dengan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140282,"Ojok peken pete kardus","Lamenne arak te dait bait selapuq","Melente jeqne sekolah bagus-bagus","Munte ndekte nyogok ndek te mauq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140283,"Ojok rau mbau tebu","Rasa' sekediq ilang manisna","Nda' bae mele te tipu","Timaq na tandang ntan sara na", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140284,"Ojok Selong gitaq tukang bangku","Tukang bangku laguq jauq sekop","Lamun tetu mele leq aku","Dateng dait aku leq bioskop", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140285,"Ojok Surabaya lalo begawe","Lalo begawe tangket Ninik","Tiang lalo endeqne suwe","Antih tiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140286,"Ojoq bangket bau paoq","Ta lokes isiq ladik merang","Timaq jaq delalo ojoq desan jaoq","Lamun unteng bedait doang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140287,"Ojoq kantor kadu dasi","Ojoq Selong beli semen","Ketimbang pada korupsi","Araqan pada ngamen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140288,"Ojoq lendang talet tebu","Dendeq lupaq da lalo pano","Lamun sida mula tetu","Tebedeit leq berugaq sino", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140289,"Ojoq Penakaq beli jangkeh","Ojoq toko beli dadu","Inaq amaq kecewa dait sedeh","Gitaq anaq jari pecandu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140290,"Olahraga di tanah lapang","Gundu bermain di lorong sempit","Segala kesulitan jadi gampang","Semangat selalu dan gesit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140291,"Olak olak kuala rekan","Pina pina di dalam dulang","Tuan bertolak adek berpesan","Jangan ber-lengah di bandar orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140292,"Olak olak kuala rekan","Pina pina di dalam dulang","Tuan bertolak adek berpesan","Jangan ber-lengah di bandar orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140293,"Olak-olak Kuala Rekan","Pinang-pinang di dalam dulang","Tuan bertolak adik berpesan","Jangan berlengah di bandar orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140294,"Olak-olak si pekan-pekan","Kuala lama menjala udang","Kakanda bertolak adinda berpesan","Jangan lama di negeri orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140295,"Ole anoman memboeka boekiet","Tanam sierie di rama-rama","Mienta Nonna sabar sedikiet","Besok ketemoe la sama-sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140296,"Ole kalaoetaen goenoeng manila","Sierie koening bertjarang tjarang","Atie sieapa bole ken loepa","Poetie koening lehernja jiendjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140297,"Ole ketoepat daoen miendanie","Rakiet mwmoeat la alang alang","Mangka sie Nonna djadie begienie","Sakiet koe boekan alang alang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140298,"Ole langkoeas die atas bangkoe","Soeleng atap lagie berboea","Belon la poeas tiedoer die pangkoe","Apa tjilaka kieta berdoea", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140299,"Ole padoepan die atas kona","Baik saya rimbang daoeloe","Kaloe tieda saroepa nona","Bear saya boedjang daoeloe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140300,"Ole salambar jatoe die antjoal","Saya die sinie bertanam padie","Atie siapa tidakan antjoer","Nonna bagoeas la baik boedie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140301,"Ole sataoon la doea tau' oon","Alap alap boea kamierie","Ibaraad angien tieoopkan dau' oon","Darie jau'e boeang kamarie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140302,"Ole soelasie dari oetoesan","Orang bergandoeng dalam serambie","Salasie jangan la die poetoeskin","Tjaboet pedang antjoerkan kamie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140303,"Ole soelasie di ayer manies","Inie goedang di dalam doelang","Tienggal Nonna djangan menangis","Soeda waktoe la saya poelang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140304,"Ole soelasie die balie biedoe","Bersoentieng tadja jambang hoeribang","Karna soeda mannangoong riendoe","Atie die dalam termimbang bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140305,"Ole tapak die dalam tapie","Tapie die dalam la lemoet poean","Minta bertemoe dalam mengiempie","Mengiempie die dalam selimoet toean", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140306,"Oleh anoman membuka bukit","Tanam sirih di rama-rama","Minta Norma sabar sedikit","Besok ketemu lah sama-sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140307,"Oleh baoenja sampe kasienie","Katjip pinang berkattie kattie","Kepada langit dan dengan boemie","Sebab moeda poenja kendatie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140308,"Oleh baunya sampai ke sini","Kacip pinang berkati-kati","Kepada langit dan dengan bumi","Sebab muda punya kendati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140309,"Oleh bersunting bunga melati","Ayam berkokok dalam perahu","Menaruh sumpah lah sampai mati","Minta jangan seorang tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140310,"Oleh dalima di bawa karang","Oleh mengistan koelitnja manies","Djangan pertjaya moeda sekarang","Orang pendjoesta moeloetnja manies", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140311,"Oleh delima di bawa karang","Oleh manggistan kulitnya manis","Jangan percaya muda sekarang","Orang pendusta mulutnya manis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140312,"Oleh delima raginya kain","Cincin permata di dalam tanah","Empat lima ada yang lain","Masa sama bagi si Norma", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140313,"Oleh disuji bantalnya tilam","Tanda-tanda batang rumbia","Tuanlah maradunkan hati di dalam","Haramnya sungguh hati di dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140314,"Oleh disuji bantalnya tilam","Tanda-tanda batang rumbia","Tuanlah maradunkan hati di dalam","Haramnya sungguh hati di dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140315,"Oleh gamparam di oekir oekir","Barsamala memboat doepa","Barang di kata djanganlahmoenkir","Nantie lang tjaya di moeka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140316,"Oleh gamparan di ukir-ukir","Rasamala membuat dupa","Barang dikata janganlah mungkir","Nanti hilang cahaya dimuka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140317,"Oleh gamparan diukir-ukir","Rasamala membuat dupa","Barang dikata janganlah mungkir","Nanti hilang cahaya di muka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140318,"Oleh itu saya berseru","Ubahlah sikap wahai bangsaku","Bangun segera dari tidurmu","Ke arah kemajuan sama dituju", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140319,"Oleh kadondong deri Malakka","Ambierken padie di atas tana","Makan berkaca la ayer mata","Menentieken Nonna terlaloe lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140320,"Oleh kedondong dari Melaka","Hamburkan padi di atas tanah","Makan berkuahlah air mata","Menantikan Norma terlalu lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140321,"Oleh kelihatan gunung Manila","Sirih kuning bercarang-carang","Hati siapa boleh ku lupa","Putih kuning lehernya jinjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140322,"Oleh kemuning daunnya kuning","Mari ku petik di tangan kanan","Nona suka saya pun ingin","Minta tandalah sapu tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140323,"Oleh ketupat daun medani","Rakit memuatlah alang-alang","Maka si Norma jadi begini","Sakit ku bukan alang kepalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140324,"Oleh koe soentieng boenga melatie","Ayam berkokok dalam peraoe","Menaro soempa la sampe matie","Mienta djangan saorang tare", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140325,"Oleh lengkuas di atas bangku","Sedang atap lagi berbuah","Belumlah puas tidur di pangku","Apa celaka kite berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140326,"Oleh manggistan kulitnya manis","Jemur padi atas mahligai","Bengkak mata saya menangis","Tidak boleh ku tahan lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140327,"Oleh mangiestan koelitnja manies","Jemoer padie atas meligie","Bengkak mata saya menangies","Teda bole koetahan lagie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140328,"Oleh melati di dalam puan","Sirih lipat di hujung kain","Rupa si Norma sebagai bulan","Jangan ingat pada yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140329,"Oleh melatie di dalam poean","Sierie liepat di oedjoeng kaen","Roepa Si Nonna sebagai boelan","Djangan ingat pada jang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140330,"Oleh pedupan di atas kona","Biar saya imbang dahulu","Kalau tiada serupa nona","Biar saya bujang dahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140331,"Oleh permata bintang benua","Celaka hati di puju kain","Sampai mati kita berdua","Hatiku tiada pada yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140332,"Oleh permata bintang binoea","Tjelaka atie di poedjoe kaen","Sampe mata kita berdoewa","Atiekoe tieda pada jang laen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140333,"Oleh selasi dari Utusan","Orang bergendang daun surambi","Kekasih janganlah di putuskan","Cabut pedang hancurkan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140334,"Oleh selasi di air manis","Sirih gading di dalam dulang","Tinggal Norma jangan menangis","Sudah waktulah saya pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140335,"Oleh selembar jatuh di ancor","Saya di sini bertanam padi","Hati siapa tiadakan hancur","Norma baguslah baik budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140336,"Oleh Tenggara lah Timur Laut","Lagi kilap lah dengan guntur","Sindir pantun tidak menyaut","Gila si muda sampai seumur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140337,"Oleh tepuk di dalam topi","Topi di dalamlah lemut puan","Minta bertemu dalam mengimpi","Mengimpi di dalam selimut tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140338,"Oleh toengara la tiemoer laoet","Lagie kilap la dengan goentoer","Siendier pantoon tida menjaoat","Gila si moeda sampe saoemoer", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140339,"Oleq leman bangket jauq bawang","Perembun kayuq isiq tunuq bate","Rajin-rajin pade sembahyang","Adeqne eraq endeq tame neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140340,"Oleq sekolah pade lelah","Ngemen aiq nyet mula maiq gati","Mangkit niki jaman susah","Ndaq ite pada korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140341,"Oli loka ri gade padondona","Sole patalaka ri karavana","Natuvu ane ledo nosabaya","Lenje nagaya leria gunana", 4, 6);
