INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (140343,"Olok-olok burung di laut","Ogak-ogak burung di bukit","Rupa elok bagai diraut","Akan penawar hati yang sakit", 4, 6);
