INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141432,"Orang tua dijunjung tinggi","Orang muda ditunjuk ajar","Lapang dada mengandung budi","Terang telinga banyak didengar", 4, 6);
