INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141442,"Orang yang jauh jangan dinanti","Kelak tuan merana badan","Salam berpaut Hati ke Hati","Gembira selalu itu harapan", 4, 6);
