INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141592,"Padi jangan dicampur antah","Melukut tanggal sekam melayang","Hatiku jangan diberi susah","Tidaklah takut tinggal seorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141593,"Padi jo banto punyo buah","Pandan timbarau malantaikan","Ati jo mato punyo ulah","Badan nan cilako marasaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141594,"Padi dan banto punya buah","Pandan teberau melantaikan","Hati dan mata punya olah","Badan nan celaka merasakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141595,"Padi keriseh condong di baruh","Habis makan Tiak Jelana","Saja nasib untung ku hodoh","Bumi luas langkah tak kena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141596,"Padi kok badarang masak","Sipuluik tuai daulu","Anak kunci sambilan pasak","Nan sudah ungkai daulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141597,"Padi kalau beransur masak","Padi pulut tuai dahulu","Anak kunci sembilan pasak","Nan sudah selesai ungkai dahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141598,"Padi layang padi hampa","Bila ditumbok sunyi senyap","Mana hati ta' kan hiba","Bonda hilang bapa lenyap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141599,"Padi lembut dari Senggora","Mari dimasak buat ketupat","Mati semut kerana gula","Mati bajak makan diangkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141600,"Padi mang cerah tak putih","Rambutan gading di dalam timba","Hati mana kami tak sedih","Kerana buatan didepan mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141601,"Padi menguning petani legalah","Saatnya untuk bergemah rifah","Menjalin hubungan dengan Allah","Melalui syari'ah faham ma'rifah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141602,"Padi merbuk padi Aceh","Hendak dijual di tengah pekan","Biarlah buruk hatinya kasih","Orang cantik tak boleh dimakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141603,"Padi merbuk padi di Acheh","Masak mari dengan padi santan","Biarlah buruk hatinya kasih","Orang cantik tak boleh di makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141604,"Padi merbuk padi di Acheh","Masak mari dengan pati santan","Biarlah buruk hatinya kasih","Orang cantik tak boleh dimakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141605,"Padi muda jangan di lurut","Kalau di lurut rosak batangnya","Hati muda jangan di turut","Kalau di turut rosak badannya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141606,"Padi muda jangan di lurut","Kalu lurut patah batang nya","Hati muda jangan di turut","Kalu di turut pada datang nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141607,"Padi muda jangan di lurut","Kalu lurut rosak batang nya","Hati muda jangan di turut","Kalu turut sala datang nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141608,"Padi muda jangan dilurut","Kalau dilurut pecah batang","Hati muda jangan diturut","Kalau diturut salah datang", 4, 6);
