INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141610,"Padi muda jangan dilurut","Kalau dilurut pecah batang","Hati muda jangan diturut","Kalau diturut salah datang", 4, 6);
