INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141612,"Padi muda jangan di-lurut","Kalau di-lurut pechah batang","Hati muda jangan di-turut","Kalau di-turut salah datang (Pantun Melayu Lama)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141613,"Padi nan habih dek panyakik","Kok tidak kuek manyiangi","Maro nan datang manyalingkik","Saba jo luruih paimbangi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141614,"Padi nan jangan dicampur antah","Melukut tinggal sekam melayang","Jangan hatiku diberi patah","Meskipun saya dagang terbuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141615,"Padi perak berdaun suasa","Buahnya mengurai emas merah","Sudah berbudi lagi berbahasa","Itulah tanda bestari bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141616,"Padi pulot di dalam bendang","Banyak rumpot darilah padi","Mulot kita di suap pisang","Buntot kita dijangkiti duri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141617,"Padi pulut dari Malaka","Bungkus dengan daun kemumu","Hati patah karena cinta","Adakah mungkin lagi bertemu", 4, 6);
