INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141622,"Padi pulut di dalam bendang","Banyak rumput dengan jerami","Mulut kita disuap pisang","Buntut kita dijangkit duri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141623,"Padi pulut di dalam ladang","Banyak rumput dari jerami","Mulut kita disuapi pisang","Ekor kita dikait duri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141624,"Padi pulut di dalam nyiru","Tampi segantang dibuat penganan","Janji mulut memang begitu","Kasih sayang dibuat mainan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141625,"Padi pulut dikacau","Kerambil muda dibelah","Kami di mulut sudahlah tahu","Bahawa adik sudah berubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141626,"Padi pulut ditanam orang","Berasnya dapat dibuat wajik","Hatiku kalut karam seorang","Bila teringat kepada adik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141627,"Padi pulut padi di Kelang","Pucuk menyulai ke Selama","Jangan di gelusuk-gelusang","Biar berjujai lama-lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141628,"Padi pulut padi di Kelang","Makan kera dengan babi","Jangan kelut jangan di gelusang","Alamat kerja tidak menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141629,"Padi pulut rendangkan berteh","Daun sa-penoh tepong tawar","Berpayong iram bergajah puteh","Asal keturunan raja Muar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141630,"Padi sa'gengam habis-lah lechoh","Tidak boleh di tumboh-nya lagi","K'hendak Allah jugak yang sunggoh","Tidak boleh k'hendak hati (Kabar Uchapan Baru) (AN. Panton Dari Malaka')", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141631,"Padi sagongam abilah locuo","Indak bulio ditumbuk lagi","Kohondak Allah juo nan sungguo","Indak dapek kohondak hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141632,"Padi sarumpun dipaduo","Atau mangguntiang siba baju","'Gadang manyimpang' caro kalimo","Dibagi duo pulo nan satu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141633,"Padi segemal baru ditugal","Beras di laman tidak terkajang","Cantik amal kerana akal","Tebal iman berfikir panjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141634,"Padi segemal kepuk di hulu","Sirih di hilir merekap junjungan","Kepalang duduk menuntut ilmu","Pasir sebutir jadikan intan", 4, 6);
