INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141636,"Padi segenggam ditumbuk luluh","Tidak boleh ditanak lagi","Kehendak Allah juga yang sungguh","Tidak boleh sekehendak hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141637,"Padi segenggam ditumbuk luluh","Tidak boleh ditanak lagi","Kehendak Allah juga yang sungguh","Tidak boleh sekehendak hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141638,"Padi segenggam habislah lecuh","Tidak boleh ditumbuk lagi","Kehendak Allah juga yang sungguh","Tidak dapat kehendak hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141639,"Padi sekarung tolong pikulkan","Hendak diangkat terlalu berat","Kalau semua ini diresapi dan diamalkan","Selamatlah hidup dunia akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141640,"Padi sepulut 'rang Melaka","Dibawa orang pergi ke Kubu","Hati putus kerana bercinta","Bilakah badan akan bertemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141641,"Padi seribu dapat dibeli","Puan dipangku Raja Mambang","Menjadi habu sekali-sekali","Tuan ada timbulnya abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141642,"Padi seribu dapat dibeli","Puan dipangku si raja mambang","Menjadi habu sekali-kali","Tuan ada timbalan abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141643,"Padi sertankay di-makan tikus","Tikus pandak kuat berlari","Kalu panday habis di-timbus","Bahru lah ombak sadar kan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141644,"Padi s'genggam habis lah lechoh","Tidak bolay di tumboh nya lagi","Ke-handak Allah jugak yang sunggoh","Tidak tolay ke-handak hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141645,"Padi sipuluik kandak udang","Jatuah badarai kalupaknyo","Adiak lah buliah ka parintang","Kami di mano ka lataknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141646,"Padi sipulut kehendak udang","Jatuh berderai kelopaknya","Adiklah beroleh perintang","Kami di mana akan letaknya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141647,"Padi sipuluik nan baaru","Karambia mudo nan babalah","Kami di muluik sudah tau","Baso adiak alah barubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141648,"Padi si-puluik rang benda-kan","Buah palo dalam parau","Manih di muluik rang bana-kan","Ati ba-alieh kami tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141649,"Padi sipuluik urang di lambah","Masak sarumpun dalam kabun","Dawek sujuik kalam manyambah","Datang karateh mintak ampun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141650,"Padi pulut orang di lembah","Masak serumpun dalam kebun","Dakwat sujud kalam menyembah","Datanglah kertas minta ampun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141651,"Padi tajamua di halaman","Dikakakan di ateh lapiak","Kok datang ayam dihalaukan","Dijamua padi sahari masiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141652,"Padi timbul di tanah puan","Burung gagak makannya padi","Hatiku betul kepada tuan","Belah dada melihat hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141653,"Padi to datang dari Palembang","Nyarislah cela belah perahunya","Lain dituju lain yang tumbang","Allah Taala juga tahunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141654,"Padi tuan batangnya kurus","Bubuhlah baja bancuh garam","Hati tuan bukannya lurus","Pepat di luar rencong di dalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141655,"Padie moeda djangan di loeroet","Kaloe di loeroet roesak batangnja","Atie moeda djangan ditoeroet","Kaloe di toroet roesak badannja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141656,"Padie timboel die tana poean","Boeroeng gagak makanan padie","Atie koe betoel kapada toean","Bela dada meliat atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141657,"Padiku lurut selasihku lurut","Jual di pekan tanah seberang","Budiku turut kasihku turut","Jangan hampakan hati orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141658,"Padinya habis dimakan pipis","Bunga senduduk disari kumbang","Sakitnya hati bukan sedikit","Duduk bimbang berdiri bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141659,"Padinya habis dimakan pipit","Bunga senduduk diseri kumbang","Sakitnya hati bukan sedikit","Duduk bimbang berdiri bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141660,"Padri lari kaki tersangkot","Jatoh tersembab di sawah padi","Apa ku ajar baik lah ikot","Jangan turut tuladan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141661,"Paduka Demang di Gunung Tahan","Mengambil akar di dalam taman","Manusia memang cari kesalahan","Walau sebesar tahi kuman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141662,"Paduka kami tidak begini","Berani datang di negeri ini","Ini tuanlah dengan Opas","Datang tiada dengan takutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141663,"Paduka Ratu ke pintu kuala","Selubung litup bersama rakan","Sementara itu majlis bermula","Acara penutup kita raikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141664,"Paduka Ratu memburu rusa","Menuju rakit tepi pangkalan","Kalau begitu keadaan memaksa","Tempahlah roket terbang ke bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141665,"Paduka ratu memburu rusa","Naik rakit tepi pangkalan","Kalau begitu keadaan memaksa","Tempahlah roket terbang ke bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141666,"Paduka ratu mencari umbut","Di hari rembang dayung bersampan","Kalau begitu dayung bersambut","Nantilah abang di ambang harapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141667,"Paduka tidak buat sengaja","Turut sudah berpangkat raja","Biar Paduka membunuh mati","Jangan buat sakit di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141668,"Paduka Tuan hendak Katanya","Cipir dirapat dalam dirobah","Jikalau Tuan tidak percaya","Biarlah beta berkalamullah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141669,"Paduka Tuan hendak ke Taya","Ceper dirapat talam terbelah","Jikalau tuan tidak percaya","Biarlah beta berkalam Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141670,"Paduka Tuan turun berida","Sayang menara becampu suga","Sukanya arib adinda","Sebab kasih diturut juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141671,"Paga nan mano den pagakan","Batuang nan indak buliah kuparang","Kapado siapo den sasakan","Untuang nan indak dapek dilarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141672,"Pagar yang mana saya pagarkan","Betungnan tak boleh kuparang","Kepada siapa saya sesalkan","Nasib yang tak dapat dilarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141673,"Pagang gadai jua bali tanah","Saizin panghulu supayo tarang","Kaampek adat bungo tanah","Saparsapuluah panghasilan tambang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141674,"Pagar bambu tanam bidara","Tumbuh rumput bercampur padi","Kita hidup seperti saudara","Sudah terikat di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141675,"Pagar bambu tanam bidara","Tumbuh rumput bercampur padi","Kita hidup seperti saudara","Sudah terikat di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141676,"Pagar besi dengan jeruji","Baik buruk boleh dikaji","Bermurah hati dalam memuji","Hati hati kalau berjanji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141677,"Pagar bilah jadikan lantai","Limpapas dalam belanga","Sabar rela hendaklah pakai","Melepas anak dagang hina", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141678,"Pagar buluh anggitnya jarang","Bunga melur kembang rajasa","Dalam sepuluh jarang seorang","Tahukan hati menimbang rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141679,"Pagar buluh anjitnya jarang","Kuntum melati bunga rajasa","Dalam sepuluh jarang seorang","Tahukan hati menimbang rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141680,"Pagar buruk jangan dicabut","Kacang rnelilit hingga ke hujung","Hati terkenang mulut menyebut","Baik budi rasa nak junjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141681,"Pagar dibuat ditempatkan angsa","Angsa dibawa dari Semarang","Karena bukan kuat kuasa","Kuasa nin ada ada yang melarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141682,"Pagar dililit kacang kara","Tebang beraksa menimpa padi","Permintaan kakanda apa bicara","Adakah ditimbang dalam hati?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141683,"Pagar dililit kajang kara","Tebang beraksa menimpa padi","Sembahnya patik apa bicara","Timbang rasa kepada hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141684,"Pagar disasak kayu berduri","Rapat-rapat jangan jarang","Menjadi hasad sehari-hari","Duduklah sahaja mengumpat orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141685,"Pagar siapa diserapkan","Pagar Kersik ke Surabaya","Pada siapa disesalkan","Sudah nasib ayah bonda berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141686,"Pagaran kampuang jo halaman","Lurahnyo babatu-batu","Ijuaknyo basaga jantan","Aianyo tanang sajak di hulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141687,"Pagar-pagar dengan kayu","Pokok limau tepi paya","Berapa saya pujuk rayu","Tuan tak mahu apa'kan daya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141688,"Pagar-pagar dengan kayu","Pokok limau tepi paya","Berapa pun dipujuk dan dirayu","Tuan tak mahu apakan daya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141689,"Pagar-pagar dengan kayu","Pokok limau tepi paya","Berapa pun dipujuk dan dirayu","Tuan tak mau apakan daya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141690,"Pager bamboe tanem bidara","Toemboe roempoet bertjampoer padie","Kita idoep saprie soedara","Soeda terikat di dalam atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141691,"Pagi berbekal kuih bingka","Petang menugal anak padi","Panjang akal peluang terbuka","Banyak modal kerja menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141692,"Pagi cemas menanti petang","Petang lara menunggu malam","Suara emas penyanyi ditatang","Bagai sutera cantik bersulam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141693,"Pagi cerah suasana damai","Banyak mata menjadi saksi","Sungguh ceria kedua mempelai","Menempuh upacara ikatan suci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141694,"Pagi dan petang berbunyi nobat","Nobat berguna bernama Khalil","Pagi dan petang hendaklah taubat","Taubat mengikut hadis dan dalil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141695,"Pagi dan petang berbunyi nobat","Nobat beragam bernama khalil","Pagi dan petang hendaklah taubat","Taubat mengikut hadis dan dalil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141696,"Pagi disambut si kicau burung","Remang senja menanti malam","Mata berkabut jiwa terkurung","Lautan duka tidak terselam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141697,"Pagi hari angin beralay","Di tiop padam api plita","Jangan s'lalu mengaku bolay","Ada kali tak turot kata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141698,"Pagi hari awam berlari","Lari sampay tanah seberang","Suda ber bahasa tambah berbudi","Selalu bolay di kenang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141699,"Pagi hari awan berkisar","Klep klep api di kota","Orang lari jangan di kejar","Kalu dia balek rosak binasa.", 4, 6);
