INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141701,"Pagi hari awan berlari","Bintang sabijit membla bukit","Suda dapat bagi di hati","Suka ria bukan sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141702,"Pagi hari ayam berkokok","Dengar sampay sa-buah negri","Kalu panday berbungah tajok","Bahru di puji si bidadari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141703,"Pagi hari bala betiki","Pekayan bebunoh sikap dibai","Bisi tinggal dudi di ai","Nurun sampai ka perau diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141704,"Pagi hari berkereta besar","Hendak ka-taman bunga kemuning","Niat hati terlalu besar","Bunga di-tanam hendak ku sunting!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141705,"Pagi hari bermain dam","Satu kala satu menang","Menengok adek bermain ram-ram","Dalam hati dudok terkenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141706,"Pagi hari diasak-asak","Untuk memasang besi keluli","Andai bestari kita memasak","Penganan pisang enak sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141707,"Pagi hari fajar bersinar","Burung berkicau kita pun suka","Setiap huruf di baca benar","Mendapat pahala berbilang marka", 4, 6);
