INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141709,"Pagi hari indah duniawi","Kedidi undan terbang ke taman","Puteri Mahsuri kisah Langkawi","Jadi teladan sepanjang zaman", 4, 6);
