INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141712,"Pagi hari memasak sayur","Sudah direndam baru dicoba","Laki bini tidak bertegur","Tengah malam raba meraba", 4, 6);
