INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141716,"Pagi hari membaca koran","Surat pembaca ada bantahan","Awal kita membaca Al Qur'an","Hendaklah berlindung kepada Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141717,"Pagi hari membaca koran","Surat pembaca ada bantahan","Awal kita membaca Al Qur'an","Hendaklah berlindung kepada Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141718,"Pagi hari membasuh kain","Kain warna bercorak awan","Cuba beri istilah lain","Bukan bunga ibarat perempuan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141719,"Pagi hari membawa keranjang","Mencari manggis bekal perjalanan","Sebab diri mengaku bujang","Kasihan gadis jangan kempunan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141720,"Pagi hari membawa keranjang","Mencari manggis bekal perjalanan","Sebab diri mengaku bujang","Takut gadis jangan kempunan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141721,"Pagi hari membeli tepung","Hendak membuat kuih bahulu","Minta maaf ketua kampung","Nak tumpang bermain semalam lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141722,"Pagi hari memikat merbah","Sambil menanti di pohon berangan","Mengapa dicari mala di lembah","Takutkan duri dalam jambangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141723,"Pagi hari memungut siput","Hendak dijual di Pekan Linggi","Badan dah tua nafas dah semput","Adakah terdaya memanjat lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141724,"Pagi hari menanak nasi","Sudah sarapan cepat kemasi","Kalau ada keliru kandungan isi dan redaksi","Mohon kesedian untuk dikoreksi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141725,"Pagi hari menanam selasih","Pergi ke teluk menarah papan","Bila terkenang budi dan kasih","Nasi sesenduk tidak termakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141726,"Pagi hari menangkap sotong","Masak gulai bercampur terong","Apa guna sangkar digantung","Kalau tidak berisi burung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141727,"Pagi hari mencari umbut","Letaknya arah tepi kuala","Tahun Masihi meriah disambut","Tahun Hijrah bagaimana pula?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141728,"Pagi hari mencuci belanga","Sampai terlupa menyiang keli","Andai saudari menjadi bunga","Pada siapa takut sekali?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141729,"Pagi hari menebang pisang","Tanam bayam jauh ke tepi","Pujuk lelaki ibarat musang","Mengira ayam di dalam mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141730,"Pagi hari mentari bersinar","Langit biru sungguh indahnya","Karena ajaran dibawa adalah benar","Banyak orang yang mengikutinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141731,"Pagi hari merumput di huma","Cari upih di kebun nan semak","Keperluan diri sedikit cuma","Jadi berlebih kerana tamak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141732,"Pagi hari nasi ditanak","Sudah masak lalu hidangkan","Tidaklah Tuhan itu beranak","Apatah lagi DIA diperanakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141733,"Pagi hari nasi ditanak","Sudah masak lalu hidangkan","Tidaklah Tuhan itu beranak","Apatah lagi DIA diperanakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141734,"Pagi hari pergi memburu","Dapat rusa berkawan-kawan","Naiklah tuan ke langit biru","Saya menunggu di kaki awan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141735,"Pagi hari pergi memukat","Dapat puyu dapat kelah","Patah kaki jalan bertongkat","Habis kayu tangga dibelah!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141736,"Pagi hari Raja bekejang","Tampong Tuan Residin Simanggang","Pemaioh perau baka ke dikalang","Sendat ai enda belapang (Pantun Sarawak)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141737,"Pagi hari tanam selasih","Pergi ke teluk menarah papan","Bila terkenang budi dan kasih","Nasi sesenduk tidak termakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141738,"Pagi hari turun ka-paya","Dapat ikan berkati-kati","Orang lelaki jangan perchaya","Lain di-bibir lain di-hati?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141739,"Pagi hari turun ke kali","Basuh sirih di dalam raga","Majlis berseri indah sekali","Terima ksih tidak terhingga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141740,"Pagi hari turun mencangkul","Mencangkul sawah dekat perigi","Sispkan hamba di dalam sanggul","Agar terbawa kemana pergi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141741,"Pagi membilang raga","Petang raga dijaja","Air penghilang dahaga","Taubat menghapus dosa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141742,"Pagi memetik buah nanas","Petangnya pergi berjaja","Anak remaja umpama tunas","Hijau subur jika dibaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141743,"Pagi mengukir rumah salji","Petang membina istana pasir","Pagi mungkir menunai janji","Petang dihina diri diusir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141744,"Pagi menjelang menyirat jaring","Cerah petang mengail ikan","Berkecai tulang dari daging","Kau kusayang kau kunantikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141745,"Pagi pagi awan berlari","Bintang sebijik membla bukit","Kalu dapat gambar di hati","Dunia di pandang tingal sedikit", 4, 6);
