INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141747,"Pagi pagi berjalan kaki","Sekali waktu bawa berenang","Tandanya iri hasad dan dengki","Tak suka melihat orang yang senang", 4, 6);
