INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141749,"Pagi pagi datang mertua","Istri pun lagi membuat ketupat","Apa salahnya berlaki tua","Badan terjamin harta pun dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141750,"Pagi pagi ke pasar minggu","Tidak lupa membeli pepaya","Kalau adik orangnya jujur","Berapa harga dolar dalam rupiah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141751,"Pagi pagi masak kanji","Makan dhulu melapekkan perut","Chakap-an kosong tiada berisi","Menjadi dusta berchampor karut", 4, 6);
