INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141755,"Pagi pagi menuai padi","Untuk mengelak panas mentari","Tuan di sana saya di sini","Sebagai penghibur pengganti diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141756,"Pagi pagi turun menjala","Dapat sepat dengan haruan","Di-kiri lipan di-kanan kala","Hampa melompat ka-pangkuan tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (141757,"Pagi petang ashik berkelahi","Membuang masa di-tepi jalan","Emak melentang anak main tahi","Itu bernama batu sengkalan", 4, 6);
